import React, {useState, useCallback, useEffect} from 'react';
import { Route } from 'react-router-dom'
import "./App.css";
import HeaderNav from './components/Layout/HeaderNav';
import Events from "./views/Events";
import Homepage from "./views/Homepage";
import UserProfile from './views/UserProfile';
import { AuthContext } from './shared/context/auth-context'

function App() {
  const [showForm, setShowForm] = useState(true);
  const [token, setToken] = useState(false)
  const [userId, setUserId] = useState(false)
  const [tokenExpirationDate, setTokenExpirationDate] = useState()


  const formHandler = (data) => {
    setShowForm(data);
    console.log(showForm);
  };

  const login = useCallback((uid, token, expiration) => {
    console.log('Hit by login "context"')
    setUserId(uid)
    setTokenE
    setToken(token)
    const tokenExpiration = expiration || new Date(new Date().getTime() + 1000 * 60 * 60 * 12) 
    localStorage.setItem('userData', JSON.stringify({userId:uid, token:token, expiration: tokenExpiration.toISOString()}))
  }, [])

  const logout = useCallback(() => {
    setToken(null)
    setUserId(null)
    localStorage.removeItem('userData')
  }, [])

  useEffect(() => {
    const localStoreageData = JSON.parse(localStorage.getItem('userData'))
    if(localStoreageData && localStoreageData.token && new Date(localStoreageData.expiration) > new Date() ){
      login(localStoreageData.userId, localStoreageData.token, new Date(localStoreageData.expiration) )
    }
  }, [login])

  return (
    <AuthContext.Provider value={{isLogged:!!token, token:token, login:login, logout:logout}}> 

      <HeaderNav onShowForm={formHandler}/>

      <Route path="/" exact> <Homepage registerOrLogin = {showForm} /></Route>
      <Route path="/events" exact> <Events/></Route>
      <Route path="/userprofile" exact> <UserProfile /> </Route>

    </AuthContext.Provider>
  );
}

export default App;
