import React, { useState } from "react";
import { Col, Row } from "react-bootstrap";
import './BadgeEditForm.css'

const BadgeEditForm = () => {
    const [selectedSport, setSelectedSport] = useState()
    
    const handleSportSelection = (e) => {
        setSelectedSport(e.target.innerText)
    }

    return (
        <Row className="sportSelectionStyle" onClick={handleSportSelection}>
            {!selectedSport}
            <Col>
                <div>Tennis</div>
                <div>Football</div>
                <div>Table Tennis</div> 
                <div>Jogging</div>
                <div>Cycling</div>
                <div>Paintball</div>
                <div>Air-Soft</div>
                <div>Skiing</div>
            </Col>
            <Col>
                <div>Basketball</div>
                <div>Workout</div>
                <div>Volleyball</div>
                <div>Badminton</div>
                <div>Ice Skating</div>
                <div>Bowling</div>
                <div>Laser Tag</div>
            </Col>
        </Row>
    )
}

export default BadgeEditForm;
