import React, {useContext} from "react";
import './Homepage.css'
import { Col, Row, Container } from "react-bootstrap";
import CarouselHomepage from "../components/Layout/CarouselHomepage";
import LoginForm from "../components/Forms/LoginForm";
import RegisterForm from "../components/Forms/RegisterForm";
import { AuthContext } from '../shared/context/auth-context'
import Image from "../assets/Images/checkingWatch.png"

const Homepage = (props) => {
  const auth = useContext(AuthContext);
  

  return (
    <React.Fragment>
      <div><img id='logo' src={Image} alt="..."/> </div>
      <div id="body1">
      <Container >
          <Row>
            {!auth.isLogged && props.registerOrLogin && <Col xl={8}><LoginForm /></Col>}
            {!props.registerOrLogin && <Col xl={8}><RegisterForm /></Col>}
            {auth.isLogged && <Col xl={8}> <h1 style={{'margin':'10rem'}}>Welcome to SportTogether now you can explore events or create them</h1></Col>}
            <Col xl={{span:4, order:'first'}}><CarouselHomepage/></Col>
          </Row>
          <Row>
            <Col xl={12} style={{'position':'relative'}}>
              
            </Col>
          </Row>
      </Container>  
      </div>
    </React.Fragment>
  );
};

export default Homepage;
