import React, {useState, useEffect} from "react";
import Select from 'react-select'
import { Form, Button, Spinner, Col,  } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { authActions } from "../../store/auth";
import { dialogActions } from "../../store/modalAndAlerts";
import "./RegisterForm.css";
import countyList from "../../utils/CitiesAndCountiesRo/countyList";
import countyCityList from "../../utils/CitiesAndCountiesRo/countyCityList";
import { userActions } from "../../store/userData";

const RegisterForm = () => {  
  const [inputEmail, setInputEmail] = useState('')
  const [inputUsername, setInputUsername] = useState('')
  const [inputFirst, setInputFirst] = useState('')
  const [inputLast, setInputLast] = useState('')
  const [inputGender, setInputGender] = useState()
  const [inputPw, setInputPw] = useState('')
  const [inputConfirmPw, setInputConfirmPw] = useState('')
  const [isLoading, setIsLoading] = useState(false)
  const [isError, setIsError] = useState()
  const [inputCounty, setInputCounty] = useState('')
  const [inputCity, setInputCity] = useState('')
  const [listCounties, setListCounties] = useState([])
  const [listCities, setListCities] = useState([])
  const dispatch = useDispatch();
  useSelector((state) => state.authentication.isAuthenticated)
  useSelector((state) => state.authentication.userId)
  useSelector((state) => state.authentication.token)
  useSelector((state) => state.authentication.tokenExpiration)
  useSelector((state) => state.dialog.modalToggle)

    useEffect(()=>{
      let lstCounties = []
      for (let i of countyList){
        lstCounties.push({value: i, label: i})
      }
      console.log(lstCounties)
      setListCounties(lstCounties)
    },[])

    
  const countyOptionChange = (obj) => {
    setInputCounty(obj.value)
    console.log(obj.value)
    const countyNameList = countyCityList[obj.value]
    console.log('LIST OF CITIES IN ARRAY: >>', countyNameList )

    let lstCities = []
      for (let i of countyNameList){
        lstCities.push({value: i, label: i})
      }
    console.log(lstCities)
    setListCities(lstCities)
  }


  const cityOptionChange = (obj) => {
    setInputCity(obj.value)
  }
  

  const registerFormHandler = async (event) => {
    event.preventDefault();
    setIsLoading(true)
    try {
      const response = await fetch('http://localhost:5000/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          'email': inputEmail,
          'username': inputUsername,
          'firstName': inputFirst,
          'lastName': inputLast,
          'county' : inputCounty,
          'city' : inputCity,
          'password': inputPw,
          'confirmPw': inputConfirmPw,
        })
      })
      const responseData = await response.json()
      if(!response.ok){
        setIsError(responseData.error)
        setIsLoading(false)
        return new Error(responseData.message)
      }else{
        let tokenExp = new Date().getTime() + (1000 * 60 * 60 * 3)
        dispatch(authActions.isAuthenticated(true)) 
        dispatch(authActions.token(responseData.token)) 
        dispatch(authActions.tokenExpiration(tokenExp))
        
        dispatch(userActions.userId(responseData.userId))
        dispatch(userActions.userCounty(responseData.county))
        dispatch(userActions.userCity(responseData.city))
        dispatch(userActions.userCreatedEvents(responseData.createdEvents))
        dispatch(userActions.userFirst(responseData.firsName))
        dispatch(userActions.userLast(responseData.lastName))
        dispatch(userActions.userGender(responseData.gender))
        dispatch(userActions.userDescription(responseData.description))
        dispatch(userActions.userLabels(responseData.labels))
        dispatch(userActions.userJoinedEvents(responseData.joinedEvents))
        dispatch(userActions.userMatchesPlayed(responseData.matchesPlayed))
        dispatch(userActions.userSportInterests(responseData.sportInterests))
        dispatch(userActions.userImages(responseData.userImages))
        dispatch(userActions.userReviewRating(responseData.reviewRating))

        localStorage.setItem('userData', JSON.stringify({userId:responseData.userId, token:responseData.token, expiration: tokenExp})) /*tokenExp it was tokenExp.toISOString() and above dates not converted to strings */
        dispatch(dialogActions.modalToggle())
        console.log(responseData)
      }
    } catch (error) {
      console.error(error)
    }
  }
 
  return (
    <div className="registerContainer">
      <div className="boxRegister">
        <div className="registerForm">
        <h3>Register</h3>
        {isError && <p className="mb-5" id="noticeEvent"> {isError}</p>}
        <Form onSubmit={registerFormHandler}>
            <Form.Text className="text-muted">
              We'll never share your data with anyone else.
            </Form.Text>
            <br/>

          <Form.Group className="mb-3" controlId="formEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" value={inputEmail} onInput={e => setInputEmail(e.target.value)} placeholder="Enter email" required/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formUserName">
            <Form.Label>User Name</Form.Label>
            <Form.Control type="text" value={inputUsername} onInput={e => setInputUsername(e.target.value)} placeholder="User name" required/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formFirstName">
            <Form.Label>First Name</Form.Label>
            <Form.Control type="text" value={inputFirst} onInput={e => setInputFirst(e.target.value)} placeholder="First name" required/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formLastName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control type="text" value={inputLast} onInput={e => setInputLast(e.target.value)} placeholder="Last name" required/>
          </Form.Group>

          <Col md="6" style={{ textAlign: "center" }}>
            <Form.Label>Level Requirement*</Form.Label>
            <div className="choiceBtns">
              <Form.Check type="radio" aria-label="option 1">
                <button
                  type="button"
                  id={buttonClass === "btnActive1" ? "btnActive1" : ""}
                  value={true}
                  onClick={btnClass1}
                >
                  Only my level{" "}
                </button>{" "}
              </Form.Check>
              <Form.Check type="radio" aria-label="option 2">
                {" "}
                <button
                  type="button"
                  id={buttonClass === "btnActive2" ? "btnActive2" : ""}
                  value={false}
                  onClick={btnClass2}
                >
                  {" "}
                  Open to everyone
                </button>{" "}
              </Form.Check>
            </div>
            {!inputLevel && (
              <div
                className="text-danger validationInput123"
                style={{ textAlign: "start" }}
              >
                {levelValidation}
              </div>
            )}
          </Col>
    
          <Form.Group controlId="formCounty">
          <Form.Label>County</Form.Label>
            <Select options ={listCounties} value={inputCounty.value} onChange={(option) => countyOptionChange(option)}/>
          </Form.Group>

          <Form.Group controlId="formCity" style={{'marginTop':'10px',}}>
          <Form.Label>City</Form.Label>
            <Select options = {listCities} value={inputCity.value} onChange={(opt) => cityOptionChange(opt)}/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formPassword" style={{'marginTop':'10px',}}>
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" value={inputPw} onInput={e => setInputPw(e.target.value)} placeholder="Password" required/>
          </Form.Group>

          <Form.Group className="mb-3" controlId="formConfirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control type="password" value={inputConfirmPw} onInput={e => setInputConfirmPw(e.target.value)} placeholder="Confirm Password" required />
          </Form.Group>

          {!isLoading && <Button variant="primary" type="submit">Register</Button>}
          {isLoading && <Button variant="primary" disabled>
            <Spinner
              as="span"
              animation="grow"
              size="sm"
              role="status"
              aria-hidden="true"
            />
            Loading...
          </Button>}
        </Form>
        </div>
      </div>
    </div>
  );
};

export default RegisterForm;
