import React, { useState } from "react";
import SearchForm from "../components/Forms/SearchForm";
import HeaderNav from "../components/Layout/HeaderNav";
import { Container, Row, Col } from "react-bootstrap";
import "./Events.css";
import UserProfileShowPage from "../components/UserProfile/UserProfileShowPage";
import FilterEvents from "../components/Events/FilterEvents";
import CreateEventLayout from "../components/Events/CreateEventLayout";
import AddEventForm from "../components/Forms/AddEventForm";
import EventList from "../components/Events/EventList";
let events_dummyData = [
  {
    id: "e1",
    Sport: "Tennis",
    Level: "Beginner",
    City: "Iasi",
    ExactLocation: "",
    Description: "",
    Date: "17-05-2021",
    Time: "18:00",
    Joined: "1/4",
    LevelRequirement: 'Everyone'
  },
  {
    id: "e2",
    Sport: "Football",
    Level: "Intermediate",
    City: "Botosani",
    ExactLocation: "BtCourt",
    Description: "Come join me!",
    Date: "13-05-2021",
    Time: "14:00",
    Joined: "2/4",
    LevelRequirement: 'Strict'

  },
  {
    id: "e3",
    Sport: "Ski",
    Level: "Beginner",
    City: "Vatra Dornei",
    ExactLocation: "Poiana Dornei",
    Description: "Hai la skiiiiii Lorem",
    Date: "19-05-2021",
    Time: "13:00",
    Joined: "3/4",
    LevelRequirement: 'Everyone'

  },
  {
    id: "e4",
    Sport: "Tennis",
    Level: "Advanced",
    City: "Iasi",
    ExactLocation: "Undeva in iasi",
    Description: "Te tine racheta?",
    Date: "17-05-2021",
    Time: "18:00",
    Joined: "1/4",
    LevelRequirement: 'Advanced'
  },
];

const Events = (props) => {
  const [showAddEvent, setShowForm] = useState(false);
  const showEventFormHandler = (data) => {
    setShowForm(data);
  };

  return (
    <div className="body">
      <HeaderNav />
      <Container className="justify-content-center">
        <Row id="search" className="justify-content-center">
          <Col xl={3}>
            <UserProfileShowPage />
          </Col>
          <Col xl={7}>
            {showAddEvent && <AddEventForm />}
            <SearchForm />
            <EventList eventsList = {events_dummyData}/>
            
          </Col>
          <Col xl={2}>
            <FilterEvents />
            <CreateEventLayout onShowEventForm={showEventFormHandler} />
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Events;
