import React, {useState} from "react";
import SearchForm from "../components/Forms/SearchForm";
import HeaderNav from "../components/Layout/HeaderNav";
import { Container, Row, Col } from "react-bootstrap";
import EventItem from "../components/Events/EventItem";
import './Events.css'
import UserProfileShowPage from "../components/UserProfile/UserProfileShowPage";
import FilterEvents from "../components/Events/FilterEvents";
import CreateEventLayout from "../components/Events/CreateEventLayout";
import AddEventForm from "../components/Forms/AddEventForm";
  let events_dummyData = [
    {id:'e1', Sport:'Tennis', Level:''},
    {
      id: "e1",
      title: "Paper",
      amount: 94.12,
      date: new Date(2020, 7, 14),
      category: 'Grocery'
    },
    {},
    {}
  ]

const Events = (props) => {
  const [showAddEvent, setShowForm] = useState(false)
  const showEventFormHandler = (data) => {
    setShowForm(data)
  }

  return (
    <div className="body">
      <HeaderNav />
      <Container className="justify-content-center" >
        <Row  id="search" className="justify-content-center">
          <Col xl={3}>
            <UserProfileShowPage />
            </Col>
          <Col xl={7}>
            {showAddEvent && <AddEventForm />}
            <SearchForm />
            <EventItem />
            <EventItem />
            <EventItem />
            <EventItem />
          </Col>
          <Col xl={2}>
            <FilterEvents />
            <CreateEventLayout onShowEventForm={showEventFormHandler}/>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Events;
