import { Alert } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { alertActions } from "../../store/alert";

const AlertDismissible = (props) => {
    const dispatch = useDispatch;
    const alertTitle = useSelector((state) => state.alert.alertTitle)
    const alertBody = useSelector((state) => state.alert.alertBody)
  
    
      return (
        <Alert variant={props.variant} onClose={() =>dispatch(alertActions.alertToggle())} dismissible>
          <Alert.Heading>{alertTitle}</Alert.Heading>
          <p>
            Change this and that and try again. Duis mollis, est non commodo
            luctus, nisi erat porttitor ligula, eget lacinia odio sem nec elit.
            Cras mattis consectetur purus sit amet fermentum.
          </p>
        </Alert>
      );
    }
