        setIsLoading(false);
        return new Error(responseData.message);
      }
    } catch (error) {
      setIsLoading(false);
      console.log(error);
    }


    setIsLoading(false)
  };
	
  return (
    <React.Fragment>

      {!responseOk && <form encType="multipart/form-data">
        {isLoading && <div>Please don't close this window while we processing your request</div>}
        <div className="wrapper"  >
          <div className="file-upload">
            <input type="file" id="fileUpload" name="profile" onChange={handleImage}/>
            <i className="fa fa-arrow-up" style={{'fontSize':'40px'}}></i>
          </div>
        </div>
        {isLoading && <Spinner animation="border" variant="primary"/>}
