import React, { useContext } from "react";
import { Navbar, Nav, Container, NavDropdown } from "react-bootstrap";
import { LinkContainer, Link } from "react-router-bootstrap";
import { AuthContext } from "../../shared/context/auth-context";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserCircle } from "@fortawesome/free-solid-svg-icons";
import "./HeaderNav.css";

const HeaderNav = () => {
  const auth = useContext(AuthContext);

  const handleLogout = () => {
    auth.logout();
  };

  const modalHandler = () => {
    auth.modalShow();
  };

  return (
    <Navbar expand="lg" sticky="top" id="navbar">
      <Container>
        <Navbar.Brand>
          <LinkContainer to="/events">
            <Navbar.Brand>Sport Together</Navbar.Brand>
          </LinkContainer>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link>
              <LinkContainer to="/events">
                <Nav.Link>Events</Nav.Link>
              </LinkContainer>
            </Nav.Link>
            <Nav.Link href="#link">Link</Nav.Link>
            <NavDropdown 
            title={ <div className="main-wrapper__profile">
                        <div className="badge1 custom">
                          <div className="circle">
                            <FontAwesomeIcon icon={faUserCircle} />
                          </div>
                        </div>
                      </div>} 
                      id="basic-nav-dropdown">

            <NavDropdown.Item href="#action/3.1">
              {auth.isLogged && (<Nav.Link onClick={handleLogout}>Logout</Nav.Link>)}
              {!auth.isLogged && (<Nav.Link variant="primary" onClick={modalHandler}>Register</Nav.Link>)}
            </NavDropdown.Item>
          <NavDropdown.Item href="#action/3.2">
            
          </NavDropdown.Item>
              
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default HeaderNav;


