import React, { useState } from "react";
import { Navbar, Nav, Container } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import "./HeaderNav.css";
const HeaderNav = (props) => {
  const [showRegister, setShowRegister] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false)
  const showFormHandler = () => {
    setShowRegister((value) => !value);
    props.onShowForm(showRegister);
  };

  if(props.isLogged === true){
    setIsLoggedIn(true)
  }

  return (
    <Navbar sticky="top" id="navbar">
      <Container>
        <LinkContainer to="/">
          <Navbar.Brand>Sport Together</Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav className="me-auto">
            <LinkContainer to="/events">
              <Nav.Link >Events</Nav.Link>
            </LinkContainer>
          </Nav>
          <Nav>
            {isLoggedIn && <Nav.Link> Log Out </Nav.Link>}
            {!isLoggedIn && !showRegister && (<Nav.Link href="#" onClick={showFormHandler}>Register</Nav.Link>)}
            {!isLoggedIn && showRegister && (<Nav.Link href="#" onClick={showFormHandler}>Login</Nav.Link>)}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default HeaderNav;
