import React, { useState, useEffect } from "react";
import './MyEvents.css';
import UserProfileCard from '../components/UserProfile/UserProfileCard';
import { Container, Row, Col } from "react-bootstrap";
import { useSelector } from "react-redux";
import CreateEventLayout from "../components/Events/CreateEventLayout";
import AddEventForm from "../components/Forms/AddEventForm";
import EventList from "../components/Events/EventList";


const MyEvents = () => {
  const [showForm, setShowForm] = useState(false);
  const [eventsList, setEventsList] = useState('')
  const [userDetails, setUserDetails] = useState('')
  
  const token = useSelector((state) => state.authentication.token)

  
  useEffect(() => {
    const fetchMyEvents = async() => {
      let response = await fetch('http://localhost:5000/events/myevents', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': 'Bearer '+token,
        }
      })
      const responseData = await response.json()
      console.log('responseData: ',responseData)
      setEventsList(responseData)
      const userData = {username: responseData.author.username, profileImg: responseData.author.profileImg}
      set
    }   
    fetchMyEvents()
  }, [token])

  const showEventFormHandler = (formBoolean) => {
    setShowForm(formBoolean);
  };
  const addEventItemHandler = (newEvent) => {
    setShowForm(newEvent)
  };

    
    return(
        <div className="body">
            <Container className="justify-content-center">
                <Row id="search" >
                <Col sm={3}>
                    <UserProfileCard eventsList = {eventsList}/>
                    <CreateEventLayout onShowEventForm={showEventFormHandler} onShowAddEvent={showForm}/>
                </Col>
                <Col md={7}>
                    {showForm && <AddEventForm onAddEventItem = {addEventItemHandler}/>}
                    <EventList eventsList = {eventsList}/>
                </Col>
                <Col xl={2}>
                    Here Something? ??? ? 
                </Col>
                </Row>
            </Container>
        </div>
    )
}

export default MyEvents;
