import { createSlice } from "@reduxjs/toolkit"

const initialDataState = {
    userId:null,
    username: null,
    userFirst: null,
    userLast: null,
    userCounty: null,
    userCity: null,
    userProfilePic: null,
    userCoverPic: null,
    description: null,
    sportInterests: null,
    createdEvents: null,
    joinedEvents: null,
    matchesPlayed: null,
    reviewRating: null
}

const userSlice = createSlice({
    name:'userData',
    initialState: initialDataState,
    reducers: {
        userId(state, action) {
            state.userId = action.payload
        },
        username(state, action) {
            state.userId = action.payload
        },
        userFirst(state, action) {
            state.userId = action.payload
        },
        userLast(state, action) {
            state.userId = action.payload
        },
        userCounty(state, action) {
            state.userId = action.payload
        },
        userCity(state, action) {
            state.userId = action.payload
        },
        userProfilePic(state, action) {
            state.userId = action.payload
        },
        userId(state, action) {
            state.userId = action.payload
        },
        userId(state, action) {
            state.userId = action.payload
        },
        userId(state, action) {
            state.userId = action.payload
        }
    }
})


export const userActions = userSlice.actions;

export default userSlice.reducer;
