import React, { useContext } from "react";
import "./Homepage.css";
import { Col, Row, Container } from "react-bootstrap";
import CarouselHomepage from "../components/Layout/CarouselHomepage";
import LoginForm from "../components/Forms/LoginForm";
import RegisterForm from "../components/Forms/RegisterForm";
import tennisImg from "../assets/Images/tennis-front.jpg";
import ModalReg from "../components/UI/ModalReg";
import { AuthContext } from "../shared/context/auth-context";

const Homepage = props => {
  const auth = useContext(AuthContext);
  const backdropFunction = () => {
    auth.modalShow();
  };

  return (
    <React.Fragment>
      <div id="welcomeBox">
        <div id="forBg">
          <div id="header">
            <div id="header-content">
              <h1>
                <a href="/allbooks?category=&rent=&reviewScore=&price=&pg=1">
                  Bibliotech <i class="fas fa-book-open"></i>
                </a>
                <a id="abc" href="/register">
                  Register{" "}
                </a>
                <a id="abc" href="/login">
                  Login{" "}
                </a>
              </h1>
            </div>
          </div>
        </div>

        <div class="container123">
          <div id="box111">
            <div>
              <span id="rent111">Rent.</span>
            </div>
            <div>
              <span id="sell111">Sell.</span>
            </div>
            <div>
              <span id="explore111">Explore.</span>
            </div>
          </div>
        </div>

        <div class="d-flex justify-content-center">
          <h3>Not registered yet?</h3>
        </div>
        <div>
          <h3 class="d-flex justify-content-center">
            Explore our website and find your favourite books.
          </h3>
        </div>
        <div>
          <h3 class="d-flex justify-content-center">
            Create an account, it only takes one minute!
            <a href="/register"> > Register now</a>
          </h3>
        </div>
      </div>
      <div>
        <img id="homepageImg" src={tennisImg} alt="..." />{" "}
      </div>
      <Container>
        <Row>
          <ModalReg
            show={props.showMod}
            onHide={backdropFunction}
            title="Please Take a min"
          >
            {" "}
            <RegisterForm />{" "}
          </ModalReg>
          <Col xl={8}>
            <LoginForm />
          </Col>
          <Col xl={{ span: 4, order: "first" }}>
            <CarouselHomepage />
          </Col>
        </Row>
      </Container>
    </React.Fragment>
  );
};

export default Homepage;
