import React, {useState, useEffect} from "react";
import './ProfileCard.css'
import { Col } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { dialogActions } from "../../store/modal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faUserCircle, faPen} from "@fortawesome/free-solid-svg-icons";


const ProfileCard = () => {
    const [showDropdown, setShowDropdown] = useState(false)
    const [showModalType, setShowModalType] = useState(0)
    const [coverModal, setCoverModal] = useState(false)
    const [userDetails, setUserDetails] = useState('')
  
  
    const dispatch = useDispatch();
  
  
    const token = useSelector((state) => state.authentication.token)
  
    const handleViewProfile = () => {
      setShowModalType(1)
      dispatch(dialogActions.modalToggle())
    }
  
    const handleChangeProfile = () => {
      setShowModalType(2)
      dispatch(dialogActions.modalTitle('Change profile picture'))
      dispatch(dialogActions.modalToggle())
    };
  
    const handleChangeCover = () => {
      setCoverModal(true)
      dispatch(dialogActions.modalTitle('Change cover picture'))
      dispatch(dialogActions.modalToggle())
    }
  
    const handleDropdown = () => {
      setShowDropdown((prevDropdown) => !prevDropdown)
    }
  
    useEffect(() => {
      const fetchProfileData = async() => {
        let response = await fetch('http://localhost:5000/userprofile', {
          method: 'GET',
          headers:{
            'Content-Type': 'application/json',
            'Authorization': 'Bearer '+token,
          }
        })
        const responseData = await response.json()
        console.log('responseData: ',responseData)
        setUserDetails(responseData)
      }
      fetchProfileData()
    }, [token])

    return(   
        <Col className="mt-1" style={{border:'1px solid black'}} md={8}>
            <img className="coverImg123" src={userDetails.coverImg} alt="cover" />
            <button onClick={handleChangeCover} className="btn btn-dark"> <FontAwesomeIcon icon={faPen}/> Edit cover photo </button>
            <div className="user-image123">
                <img className="profileImg123" src={userDetails.profileImg} alt="profile" />
                <div className="after123" onClick={handleDropdown}>
                    <FontAwesomeIcon icon={faEdit} />
                </div>
            </div>
            {showDropdown &&
            <div className="box2 sb10">
                <div className='listDropdown' onClick={handleViewProfile}><FontAwesomeIcon icon={faUserCircle} style={{'float':'left', 'fontSize':'22px'}}/><span>View profile picture</span></div>
                <div className='listDropdown' onClick={handleChangeProfile}><FontAwesomeIcon icon={faEdit} style={{'float':'left', 'fontSize':'22px'}}/><span>Change profile picture</span></div>
            </div>}
        </Col>

    )
}

export default ProfileCard
