import React, { useState, useRef } from "react";

import {
  FormCheck,
  FormLabel,
  FormControl,
  Button,
} from "react-bootstrap";
import "./FilterEvents.css";
const FilterEvents = (props) => {
  filters= {
    sportFilter: "",
    skillFilter: ""
  }
  const [filters, setFilters] = useState(this.state)
  let enteredDate1;

  const sportHandler = (e) => {
    const sportFilter = e.target.value
    setFilters(...filters, sportFilter)
    props.onAddFilter(filters)
  };
  const skillHandler = (e) => {
    const skillFilter = e.target.value
    setFilters(...filters, skillFilter)
    props.onAddFilter(filters)
  };
  const dateHandler = (e) => {

  };
  return (
    <div className="filterSportsRadio">
      <div className="sportsContainer" onChange={sportHandler}>
        <FormLabel>
          <h5>Sport:</h5>
        </FormLabel>
        <FormCheck type="radio" label="Tennis" value="Tennis" name="group1" />
        <FormCheck type="radio" label="Football" value="Football" name="group1" />
        <FormCheck type="radio" label="Jogging" value="Jogging" name="group1" />
      </div>
      <div className="sportsContainer" onChange={skillHandler}>
        <FormLabel>
          <h5>Skill level:</h5>
        </FormLabel>
        <FormCheck type="radio" label="Beginner" value="Beginner" name="group2" />
        <FormCheck type="radio" label="Intermediate" value="Intermediate" name="group2" />
        <FormCheck type="radio" label="Pro" value="Pro" name="group2" />
      </div>
      <div className="sportsContainer" onChange={dateHandler}>
        <FormLabel>
          <h5>Between:</h5>
        </FormLabel>
        <FormControl type="date" ref={enteredDate1} />
        <FormControl
          className="my-1"
          type="date"
          min={enteredDate1}
          max="2022-02-01"
        />
        <Button> Submit</Button>
      </div>
    </div>
  );
};

export default FilterEvents;
