import React, { useEffect, useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faFutbol, faTableTennis, faRunning, faDumbbell, faBowlingBall, faSkating, faVolleyballBall, faSkiing } from '@fortawesome/free-solid-svg-icons'
import { Col, Row, Container } from "react-bootstrap";
import blaster from '../../assets/Images/blaster.png'
import './LabelLayout.css'
import SportLabel from "./SportLabel";

const LabelLayout = (props) => {
    const [sports, setSports] = useState([null])
    // LIST OF COLORS
    // "badge yellow"
    // "badge orange"
    // "badge pink"
    // "badge red"
    // "badge purple"
    // "badge teal"
    // "badge blue"
    // "badge blue-dark"
    // "badge green"
    // "badge green-dark"
    // "badge dark-turquoise"
    // "badge gold"
    // "badge dark"
    // "badge red-dark
    // "badge purple-dark"
    // "badge pink-dark"

    useEffect(() => {
        setSports(props.sports)
            
            console.log('AAAAA',sports)
       
    }, [props.sports])

    const handleBadge = () => {
       console.log('FOTBAL')
    }

    return(
        
        <Container>
            <Button> </Button>
            <Row id="label__row">
                <Col><SportLabel className= {sports.Football === 'notAssigned' ? "badge purple-dark disabled" : "badge purple-dark"} symbol={<FontAwesomeIcon icon={faFutbol} id="football"/>}  skillLevel="Recreational"/></Col>
                <Col><SportLabel className= {sports.Tennis === 'notAssigned' ? "badge green disabled" : "badge green"} symbol={<img src="https://img.icons8.com/ios-filled/25/000000/tennis.png" id="tennis" alt="tennis"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {sports.Jogging === 'notAssigned' ? "badge green-dark disabled" : "badge green-dark"} symbol={<FontAwesomeIcon icon={faRunning} id="jogging"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {sports.Basketball === 'notAssigned' ? "badge teal disabled" : "badge teal"} symbol={<img src="https://img.icons8.com/glyph-neue/30/000000/basketball.png" id="basketball" alt="basketball"/>} skillLevel="Beginner"/></Col>
            </Row>
            <Row id="label__row">
                <Col><SportLabel className= {sports.Workout === 'notAssigned' ? "badge red disabled" : "badge red"} symbol={<FontAwesomeIcon icon={faDumbbell} id="workout"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {sports.Bowling === 'notAssigned' ? "badge dark disabled" : "badge dark"} symbol={<FontAwesomeIcon icon={faBowlingBall} id="bowling"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {sports.Paintball === 'notAssigned' ? "badge blue disabled" : "badge blue"} symbol={<img src="https://img.icons8.com/ios-filled/30/000000/paintball-gun.png" id="paintball" alt="paintball" />} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {sports.AirSoft === 'notAssigned' ? "badge purple disabled" : "badge purple"} symbol={<img src="https://img.icons8.com/ios-filled/30/000000/sports-gun.png" id="airsoft" alt="airsoft"/>} skillLevel="Beginner"/></Col>
            </Row>
            <Row id="label__row">
                <Col><SportLabel className= {sports.LaserTag === 'notAssigned' ? "badge orange disabled" : "badge orange"} symbol={<img src={blaster} id="lasertag" alt="lasertag"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {sports.IceSkating === 'notAssigned' ? "badge gold disabled" : "badge gold"} symbol={<FontAwesomeIcon icon={faSkating} id="iceskating"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {sports.Skiing === 'notAssigned' ? "badge dark-turquoise disabled" : "badge dark-turquoise"} symbol={<FontAwesomeIcon icon={faSkiing} id="skiing"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {sports.TableTennis === 'notAssigned' ? "badge pink disabled" : "badge pink"} symbol={<FontAwesomeIcon icon={faTableTennis} id="tabletennis"/>} skillLevel="Intermediate"/></Col>
            </Row>
            <Row id="label__row">
                <Col><SportLabel className= {sports.Cycling ==='notAssigned' ? "badge blue-dark disabled" : "badge blue-dark"} symbol={<img src="https://img.icons8.com/ios-filled/30/000000/cycling-road--v1.png" id="cycling" alt="cycling"/>} skillLevel="Advanced"/></Col>    
                <Col><SportLabel className= {sports.Volleyball === 'notAssigned' ? 'badge red-dark disabled' : 'badge red-dark'} symbol={<FontAwesomeIcon icon={faVolleyballBall} id="volleyball"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {sports.Badminton === 'notAssigned' ? 'badge yellow disabled' : 'badge yellow'} symbol={<img src="https://img.icons8.com/ios-filled/29/000000/badminton.png" id="badminton" alt="badminton"/>} skillLevel="Beginner"/></Col>
            </Row>
        </Container>
    )
    
}

export default LabelLayout;
