import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faFutbol, faTableTennis, faRunning, faDumbbell, faBowlingBall, faSkating, faVolleyballBall, faSkiing } from '@fortawesome/free-solid-svg-icons'
import { Col, Row, Container } from "react-bootstrap";
import blaster from '../../assets/Images/blaster.png'
import './LabelLayout.css'
import SportLabel from "./SportLabel";

const LabelLayout = () => {
    // LIST OF COLORS
    // "badge yellow"
    // "badge orange"
    // "badge pink"
    // "badge red"
    // "badge purple"
    // "badge teal"
    // "badge blue"
    // "badge blue-dark"
    // "badge green"
    // "badge green-dark"
    // "badge silver"
    // "badge gold"
    // "badge dark"
    // "badge red-dark"
    // "badge purple-dark"
    // "badge pink-dark"

    


    const handleBadge = (e) => {
        if(e.target.className === ''){
            console.log('its empty')
            console.log(e.target)
        }else{
            console.log('its FULL')
            if(e.target.outerHTML.includes('football')){
                console.log('HAS FOOTBALL')
            }
        }
    }
    return(
        
        <Container onClick={handleBadge}>
            <Row id="label__row">
                <Col ><SportLabel className= "badge purple-dark" symbol={<FontAwesomeIcon icon={faFutbol} id="football"/>}  skillLevel="Recreational"/></Col>
                <Col><SportLabel className= "badge green" symbol={<img src="https://img.icons8.com/ios-filled/25/000000/tennis.png" alt="tennis"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge green-dark" symbol={<FontAwesomeIcon icon={faRunning}/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge teal" symbol={<img src="https://img.icons8.com/glyph-neue/30/000000/basketball.png" alt="basketball"/>} skillLevel="Beginner"/></Col>
            </Row>
            <Row id="label__row">
                <Col><SportLabel className= "badge red" symbol={<FontAwesomeIcon icon={faDumbbell}/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge dark" symbol={<FontAwesomeIcon icon={faBowlingBall}/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge blue" symbol={<img src="https://img.icons8.com/ios-filled/30/000000/paintball-gun.png" alt="paintball" />} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge purple" symbol={<img src="https://img.icons8.com/ios-filled/30/000000/sports-gun.png" alt="airsoft"/>} skillLevel="Beginner"/></Col>
            </Row>
            <Row id="label__row">
                <Col><SportLabel className= "badge orange" symbol={<img src={blaster} alt="lasertag"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge gold" symbol={<FontAwesomeIcon icon={faSkating} />} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge silver" symbol={<FontAwesomeIcon icon={faSkiing} />} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge pink" symbol={<FontAwesomeIcon icon={faTableTennis}/>} skillLevel="Intermediate"/></Col>
            </Row>
            <Row id="label__row">
                <Col><SportLabel className= "badge blue-dark" symbol={<img src="https://img.icons8.com/ios-filled/30/000000/cycling-road--v1.png" alt="cycling"/>} skillLevel="Advanced"/></Col>    
                <Col><SportLabel className= "badge red-dark" symbol={<FontAwesomeIcon icon={faVolleyballBall}/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge yellow" symbol={<img src="https://img.icons8.com/ios-filled/29/000000/badminton.png" alt="badminton"/>} skillLevel="Beginner"/></Col>
            </Row>
        </Container>
    )
    
}

export default LabelLayout;
