import React, { useEffect} from 'react';
import { Route, Switch, Redirect } from 'react-router-dom'
import { useSelector, useDispatch } from "react-redux";
import { authActions } from './store/auth'; 
import "./App.css";
import HeaderNav from './components/Layout/HeaderNav';
import Events from "./views/Events";
import Homepage from "./views/Homepage";
import UserProfile from './views/UserProfile';
// import MyEvents from './views/MyEvents';

function App() {

  const dispatch = useDispatch();
  const isAuth   = useSelector((state) => state.authentication.isAuthenticated)
  const token    = useSelector((state) => state.authentication.token)
  const tokenExp = useSelector((state) => state.authentication.tokenExpiration)
  useSelector((state) => state.authentication.userId)


  useEffect(() => {
    let logoutTimer;
    if(token && tokenExp){
      const getRemainingTime = tokenExp - new Date().getTime() / 1000;
      console.log(getRemainingTime)
      logoutTimer = setTimeout(dispatch(authActions.isAuthenticated(false)), getRemainingTime);
    } else {
      clearTimeout(logoutTimer)
    }
  },[tokenExp, token, isAuth, dispatch])

  useEffect(() => {
    const localStoreageData = JSON.parse(localStorage.getItem('userData'))
    if(localStoreageData && localStoreageData.token && localStoreageData.expiration > new Date().getTime() / 1000){
      // login(localStoreageData.userId, localStoreageData.token, new Date(localStoreageData.expiration))
      console.log(localStoreageData.token, 'EXP>', localStoreageData.expiration)

      dispatch(authActions.isAuthenticated(true));
      dispatch(authActions.token(localStoreageData.token));
      dispatch(authActions.tokenExpiration(localStoreageData.expiration))
    }
  }, [isAuth, dispatch, token, tokenExp])

  let routes;
  if(isAuth && token){
    routes= (
      <Switch>
        <Route path="/events"><Events/></Route>
        {/* <Route path="/events/myevents"><MyEvents/> </Route> */}
        <Route path="/userprofile" > <UserProfile /> </Route>
        <Redirect to="/events"/>
      </Switch>
    )
  } else {
    routes = (
      <Switch>
        <Route path="/" > <Homepage/></Route>
        <Redirect to="/"/>
      </Switch>
    )
  }

  return (
    <React.Fragment> 
      <HeaderNav/>
        {routes}
    </React.Fragment>
  );
}

export default App;
