import React, {useState} from "react";
import { Button, Spinner } from "react-bootstrap";
import { useSelector } from "react-redux";
import "./UploadIcon.css";

const UploadIcon = () => {
  const [isLoading, setIsLoading] = useState(false)
  const [selectedFile, setSelectedFile] = useState()

  const token = useSelector((state) => state.authentication.token)

  const handleImage = (event) => {
		setSelectedFile(event.target.files[0]);
	};

  const handleImageUpload = async (event) => {
    setIsLoading(true);

    const formData = new FormData();
		formData.append('profile', selectedFile);

    console.log(formData, 'formdata from fetch')

    try {
      const response = await fetch('http://localhost:5000/uploadimage',
        {
          method: 'POST',
          headers:{'Authorization': "Bearer " + token},
          body: formData,
        }
      )
      const responseData = await response.json()
      if (response.ok) {
        console.log("response.OK from /userimage > responnseData :",responseData);
        // isSuccess used to show message
      } else {
        setIsLoading(false);
        return new Error(responseData.message);
      }
    } catch (error) {
      setIsLoading(false);
      console.log(error);
    }


    setIsLoading(false)
  };
	
  return (
    <form encType="multipart/form-data">
      <div className="wrapper"  >
        <div className="file-upload">
          <input type="file" id="fileUpload" name="profile" onChange={handleImage}/>
          <i className="fa fa-arrow-up" style={{'fontSize':'40px'}}></i>
        </div>
      </div>
      {isLoading && <Spinner animation="border" variant="primary"/>}
     {isLoading && <Button onClick={handleImageUpload} style={{borderColor:'transparent'}}>Preview</Button>}
      <Button onClick={handleImageUpload} style={{borderColor:'transparent'}}>Preview</Button>
    </form>
  );
};


export default UploadIcon;
