import React, { useState, useContext } from "react";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { AuthContext } from '../../shared/context/auth-context'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserCircle } from "@fortawesome/free-solid-svg-icons";
import "./HeaderNav.css";

const HeaderNav = (props) => {
  const [showForm, setShowForm] = useState(false);

  const showFormHandler = () => {
    setShowForm((value) => !value);
    props.onShowForm(showForm);
  };

  const handleLogout = () => {
    auth.logout()
  }
  const auth = useContext(AuthContext);
  
  const setModal = () => {

  }


  return (
    <Navbar sticky="top" id="navbar">
      <Container>
        <LinkContainer to="/events">
          <Navbar.Brand>Sport Together</Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">

          <Nav className="me-auto">
            <LinkContainer to="/events">
              <Nav.Link >Events</Nav.Link>
            </LinkContainer>
          </Nav>
          <Nav>
            <LinkContainer to="/userprofile">
              <Nav.Link >
              <div className="main-wrapper__profile">
                <div className="badge1 custom">
                  <div className="circle"><FontAwesomeIcon icon={faUserCircle}/></div>
                </div>
              </div>  
              </Nav.Link>
            </LinkContainer>
            
          </Nav>
          <Nav>
            {auth.isLogged && <Nav.Link onClick={handleLogout}>Logout</Nav.Link>}
            {!auth.isLogged && !showForm && <Nav.Link onClick={showFormHandler}>Register</Nav.Link>}
            {!auth.isLogged && <Button variant="primary" onClick={setModal}> Register!!</Button>}
            {!auth.isLogged && showForm && <Nav.Link onClick={showFormHandler}>Login</Nav.Link>}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default HeaderNav;
