import React, { useEffect} from 'react';
import { Route, Switch, Redirect } from 'react-router-dom'
import { useSelector, useDispatch } from "react-redux";
import { authActions } from './store/index'; 
import "./App.css";
import HeaderNav from './components/Layout/HeaderNav';
import Events from "./views/Events";
import Homepage from "./views/Homepage";
import UserProfile from './views/UserProfile';
// import MyEvents from './views/MyEvents';
// import { AuthContext } from './shared/context/auth-context'

function App() {
  // const [modalShow, setModalShow] = useState(false);

  const dispatch = useDispatch();
  const isAuth = useSelector((state) => state.authentication.isAuthenticated)
  const token  = useSelector((state) => state.authentication.token)
  useSelector((state) => state.authentication.token)
  const tokenExp = useSelector((state) => state.authentication.tokenExpiration)

  //this done in login form with redux

  // const login = useCallback((uid, token, expiration) => {
  //   console.log('Hit by login "context"')
  //   setUserId(uid)
  //   setToken(token)
  //   const tokenExpiration = expiration || new Date(new Date().getTime() + 1000 * 60 * 60 * 12 ) 
  //   setTokenExpirationDate(tokenExpiration)
  //   localStorage.setItem('userData', JSON.stringify({userId:uid, token:token, expiration: tokenExpiration.toISOString()}))
  // }, [])

  // this done in header nav with redux 

  // const logout = useCallback(() => {
  //   setToken(null)
  //   setUserId(null)
  //   setTokenExpirationDate(null)
  //   localStorage.removeItem('userData')
  //   localStorage.removeItem('userLoc')
  // }, [])

  //
  useEffect(() => {
    let logoutTimer;
    if(token && tokenExp){
      const getRemainingTime = tokenExp - new Date().getTime() / 1000;
      logoutTimer = setTimeout(dispatch(authActions.isAuthenticated(false)), getRemainingTime);
    } else {
      clearTimeout(logoutTimer)
    }
  },[tokenExp, token, isAuth, dispatch])

  useEffect(() => {
    const localStoreageData = JSON.parse(localStorage.getItem('userData'))
    if(localStoreageData && localStoreageData.token && localStoreageData.expiration > new Date().getTime() / 1000){
      // login(localStoreageData.userId, localStoreageData.token, new Date(localStoreageData.expiration))
      dispatch(authActions.isAuthenticated(true));
      dispatch(authActions.token(token));
      dispatch(authActions.tokenExpiration(tokenExp));

    }
  }, [isAuth, dispatch, token, tokenExp])




  // const showModal = useCallback(() => {
  //   setModalShow(!modalShow)
  // },[modalShow])


  let routes;
  if(isAuth===true && token){
    routes= (
      <Switch>
        <Route path="/events"><Events/></Route>
        {/* <Route path="/events/myevents"><MyEvents/> </Route> */}
        <Route path="/userprofile" > <UserProfile /> </Route>
        <Redirect to="/events"/>
      </Switch>
    )
  } else {
    routes = (
      <Switch>
        <Route path="/" > <Homepage/></Route>
        <Redirect to="/"/>
      </Switch>
    )
  }

  return (
    <React.Fragment> 
      <HeaderNav/>
        {routes}
    </React.Fragment>
  );
}

export default App;
