import React, {useState, useCallback} from 'react';
import { Route } from 'react-router-dom'
import "./App.css";
import HeaderNav from './components/Layout/HeaderNav';
import Events from "./views/Events";
import Homepage from "./views/Homepage";
import UserProfile from './views/UserProfile';
import { AuthContext } from './shared/context/auth-context'

function App() {
  const [form, setForm] = useState(true);
  const [isLogged, setIsLogged] = useState(false)

  const formHandler = (data) => {
    setForm(data);
    console.log(form);
  };

  const login = useCallback(() => {
    setIsLogged(true)
  }, [])

  const logout = useCallback(() => {
    setIsLogged(false)
  }, [])

  return (
    <AuthContext.Provider value={{isLogged:isLogged, login:login, logout:logout}}> 
      <HeaderNav onShowForm={formHandler} loggedData={logged}/>
      <Route path="/" exact> <Homepage /></Route>
      <Route path="/events" exact> <Events/> </Route>
      <Route path="/userprofile" exact> <UserProfile /> </Route>
    </AuthContext.Provider>
  );
}

export default App;
