import './CreateEventLayout.css'
import {Button} from 'react-bootstrap'
import { useState } from 'react'
const CreateEventLayout = (props) => {
    const [showForm, setShowForm] = useState(true)

    const onAddEventForm = () => {
        setShowForm((currentShowForm) => !currentShowForm)
        props.onShowEventForm(showForm)
    }
    
    return(
        

                <Button className="btn-light center" onClick={onAddEventForm}>Create new event {props.onShowAddEvent ? '-' : '+'}</Button>
            
            
       
    )
}


export default CreateEventLayout;
