import { createStore } from 'redux';
import { createSlice } from '@reduxjs/toolkit'

const initialAuthState = {
    isLogged: false,
    userId:null,
    token:null,
    modalShow: false,
    login: () => {},
    logout: () => {}
}

createSlice({
    name:'isLogged'
})

const authorization = (state = initialAuthState, action ) => {
    if(action.type === 'login'){
        return { 
            isLogged: state.isLogged = action.value,
        }
    }
}





