import React, { useState } from "react";
import { Col, Row, Button } from "react-bootstrap";
import './BadgeEditForm.css'

const BadgeEditForm = () => {
    const [selectedSport, setSelectedSport] = useState()
    const [selectedSkill, setSelectedSkill] = useState()
    
    const handleSportSelection = (e) => {
        setSelectedSport(e.target.innerText)
    }

    const handleSkillSelection = (e) => {
        setSelectedSkill(e.target.innerText)
    }


    const submitHandler = async event => {
        try {
            const response = await fetch("http://localhost:5000/myprofile/editbadge", {
                method: "POST",
                headers: new Headers({
                  Authorization: "Bearer " + token,
                  Accept: "application/json",
                  "Content-Type": "application/json",
                }),
                body: JSON.stringify({
                    Sport: selectedSport,
                    Skill: selectedSkill,
                }),
            })
            const responseData = await response.json();
            if(response.ok){
                console.log('responseData badge>', responseData)

            }

        } catch (error) {
            
        }
    }

    return (
        <React.Fragment>

            <Row className="selectionStyle">
                {!selectedSport && 
                    <>
                        <div id="selectedSport123"> Choose the sport badge you want to edit </div>
                        <Col>
                            <div onClick={handleSportSelection}>Tennis</div>
                            <div onClick={handleSportSelection}>Football</div>
                            <div onClick={handleSportSelection}>Table Tennis</div> 
                            <div onClick={handleSportSelection}>Jogging</div>
                            <div onClick={handleSportSelection}>Cycling</div>
                            <div onClick={handleSportSelection}>Paintball</div>
                            <div onClick={handleSportSelection}>Air-Soft</div>
                            <div onClick={handleSportSelection}>Skiing</div>
                        </Col>
                        <Col>
                            <div onClick={handleSportSelection}>Basketball</div>
                            <div onClick={handleSportSelection}>Workout</div>
                            <div onClick={handleSportSelection}>Volleyball</div>
                            <div onClick={handleSportSelection}>Badminton</div>
                            <div onClick={handleSportSelection}>Ice Skating</div>
                            <div onClick={handleSportSelection}>Bowling</div>
                            <div onClick={handleSportSelection}>Laser Tag</div>
                        </Col>
                    </>
                }
            </Row>
          
            <div className="selectionStyle">
            {
                selectedSport && 
                <>
                    <div id="selectedSport123">Selected sport: {selectedSport}</div>
                    {!selectedSkill &&
                    <>
                    <div> Choose your skill level:</div>
                    <div onClick={handleSkillSelection}>
                        <div>Recreational</div>
                        <div>Beginner</div>
                        <div>Intermediate</div>
                        <div>Advanced</div>
                    </div>
                    </>
                    }
                    {selectedSkill && 
                    <>
                    <div id="selectedSport123"> Skill level: {selectedSkill} </div>
                    <Button variant="dark" type="button" onClick={submitHandler}>Submit changes</Button>
                    </>
                    }
                </>
            }
            </div>
        </React.Fragment>
    )
}

export default BadgeEditForm;
