import "./EventItem.css";

const EventItem = (props) => {
  function capitalizeFirstLetter(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }
  
  var date = "2016-10-15";
  date = date.split("-").reverse().join("-");
  return (
    <div className="cardEvent">
      <div className="profileImg">
        <img src="https://images.unsplash.com/photo-1610216705422-caa3fcb6d158?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NDB8fGZhY2V8ZW58MHwyfDB8fA%3D%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60" alt="..."/>
        <div className="userName">
          <h2>{props.author}</h2>
          <h4>@{props.author.split('-')[0]}</h4>
        </div>
      </div>
      <div className="infos">
        <span className="userTextSport">{props.sport}</span><span id="tagLevel">#{props.level}</span>
        <div className="userTextCity">{capitalizeFirstLetter(props.city)} </div>{props.location.length >= 2 ? <span> Place: {props.location}</span> : null}
        <div className="userTextDescription">{props.description}</div>
        <ul className="eventStats" style={{ paddingLeft: "0rem" }}>
          <li>
            <h3>Date</h3>
            <h4>{props.date}</h4>
          </li>
          <li>
            <h3>Start:</h3>
            <h4>{props.time}</h4>
          </li>
          <li>
            <h3 >Joined</h3>
            <h4>1/{props.nrPlayers}</h4>
          </li>
          <li>
            <h3 style={{'width': '100px'}}>Who can join:</h3>
            <h4>{props.allowed === false ? 'Everyone' : `${props.level}`}</h4>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default EventItem;
