import "./UserProfileShowPage.css";
import React, { useState } from "react";
import { Card, ListGroup, ListGroupItem, Button } from "react-bootstrap";
import NotificationRequests from "./NotificationRequests";
const UserProfileShowPage = () => {
  const [toggleRequests, setToggleRequests] = useState(false)
  const toggleHandler = () => {
    setToggleRequests(!toggleRequests)
  }

  return (
    <Card style={{ width: "20rem" }} className="cardProfile">
      <div id="imageSettings">
        <Card.Img
          variant="top"
          src="https://images.unsplash.com/photo-1610216705422-caa3fcb6d158?ixid=MnwxMjA3fDB8MHxzZWFyY2h8NDB8fGZhY2V8ZW58MHwyfDB8fA%3D%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60"
        />
      </div>
      <Card.Body>
        <Card.Title className="text-center">User Name</Card.Title>
      </Card.Body>
      <ListGroup className="list-group-flush">
        <ListGroupItem className="cardFont text-center" onClick={toggleHandler}>
          <h6>Message requests!</h6>
        </ListGroupItem>
      
      {toggleRequests &&  <NotificationRequests /> }
      {!toggleRequests &&  <NotificationRequests /> }

      
        <ListGroupItem className="text-center">
          <h6>No current Events to show</h6>
        </ListGroupItem>
        

      </ListGroup>
     
    </Card>
  );
};

export default UserProfileShowPage;
