import React, {useState} from "react";
import { Button, Spinner } from "react-bootstrap";
import { useSelector } from "react-redux";
import "./UploadIcon.css";

const UploadIcon = () => {
  const [isLoading, setIsLoading] = useState(false)

  const token = useSelector((state) => state.authentication.token)


  const handleImageUpload = event => {
    event.preventDefault()
    setIsLoading(true);
    const fileInput = document.querySelector('#fileUpload') ;
    const formData = new FormData();
    formData.append('file', fileInput.files[0]);

    console.log(formData, 'formdata from fetch')
    fetch('http://localhost:5000/uploadImage', {
      method: 'POST',
      body: formData
    })
    // .then(response => response.json())
    // .then(data => {
    //   console.log(data)
    //   return setIsLoading(false)
    // })
    // .catch(error => {
    //   console.error(error)
    //   return setIsLoading(false)
    // })
    setIsLoading(false)
  }

  return (
    <form encType="multipart/form-data" onSubmit={handleImageUpload}>
      <div className="wrapper">
        <div className="file-upload">
          <input type="file" id="fileUpload" name="image" onChange={handleImage}/>
          <i className="fa fa-arrow-up" style={{'fontSize':'40px'}}></i>
        </div>
      </div>
      {isLoading && <Spinner animation="border" variant="primary"/>}
      <Button type='submit' style={{borderColor:'transparent'}}>Preview</Button>
    </form>
  );
};


export default UploadIcon;
