import { createSlice } from "@reduxjs/toolkit"

const initialDataState = {
    userId:null,
    username: null,
    userFirst: null,
    userLast: null,
    userCounty: null,
    userCity: null,
    userImages: null,
    userDescription: null,
    userDescription: null,
    userSportInterests: null,
    userCreatedEvents: null,
    userJoinedEvents: null,
    userMatchesPlayed: null,
    userReviewRating: null
}

const userSlice = createSlice({
    name:'userData',
    initialState: initialDataState,
    reducers: {
        userId(state, action) {
            state.userId = action.payload
        },
        username(state, action) {
            state.username = action.payload
        },
        userFirst(state, action) {
            state.userFirst = action.payload
        },
        userLast(state, action) {
            state.userLast = action.payload
        },
        userCounty(state, action) {
            state.userCounty = action.payload
        },
        userCity(state, action) {
            state.userCity = action.payload
        },
        userImages(state, action) {
            state.userProfilePic = action.payload
        },
        userDescription(state, action) {
            state.userDescription = action.payload
        },
        userSportInterests(state, action) {
            state.userSportInterests = action.payload
        },
        userCreatedEvents(state, action) {
            state.userCreatedEvents = action.payload
        },
        userJoinedEvents(state, action) {
            state.userJoinedEvents = action.payload
        },
        userMatchesPlayed(state, action) {
            state.userMatchesPlayed = action.payload
        },
        userReviewRating(state, action) {
            state.userReviewRating = action.payload
        }
    }
})


export const userActions = userSlice.actions;

export default userSlice.reducer;
