import React, { useState } from "react";
import './BadgeEditForm.css'

const BadgeEditForm = () => {
    const [selectedSport, setSelectedSport] = useState()
    
    const handleSportSelection = (e) => {
        setSelectedSport(e.target.innerText)
    }

{
    return (
        <div className="sportSelectionStyle" onClick={handleSportSelection}>
            <div>Tennis</div>
            <div>Football</div>
            <div>Table Tennis</div> 
            <div>Jogging</div>
            <div>Cycling</div>
            <div>Paintball</div>
            <div>Air-Soft</div>
            <div>Skiing</div>
            <div>Basketball</div>
            <div>Workout</div>
            <div>Volleyball</div>
            <div>Badminton</div>
            <div>Ice Skating</div>
            <div>Bowling</div>
            <div>Laser Tag</div>
        </div>
    )
}
}

export default BadgeEditForm;
