import "./AddEventForm.css";
import React, { useState } from "react";
import countyList from "../../assets/CountyList/countyList";
import { Spinner } from "react-bootstrap";
import {
  Form,
  Col,
  Row,
  Button,
  FloatingLabel,
  FormControl,
} from "react-bootstrap";


var today = new Date();
var dd = today.getDate();
var mm = today.getMonth()+1; //January is 0 so need to add 1 to make it 1!
var yyyy = today.getFullYear();
if(dd<10){
  dd='0'+dd
} 
if(mm<10){
  mm='0'+mm
} 

today = yyyy+'-'+mm+'-'+dd;


const AddEventForm = (props) => {
  const [isLoading, setIsLoading] = useState(false)
  const [validated, setValidated] = useState(false);
  const [inputSport, setInputSport] = useState('')
  const [inputNrPlayers, setInputNrPlayers] = useState('')
  const [inputLevel, setInputLevel] = useState('')
  const [inputCounty, setInputCounty] = useState('')
  const [inputCity, setInputCity] = useState('')
  const [inputPlace, setInputPlace] = useState('')
  const [inputDate, setInputDate] = useState('')
  const [inputTime, setInputTime] = useState('')
  const [inputDescription, setInputDescription] = useState('')





  const handleSubmit = async (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
    }
    setValidated(true);
    if (form.checkValidity() === true) {
      event.preventDefault();
      
      try {
        setIsLoading(true)
        const response = await fetch('http://localhost:5000/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
            // 'Content-Type': 'application/x-www-form-urlencoded',
          },
          body: JSON.stringify({
            'Sport': inputEmail,
            'County': inputPw,
            'City': rememberMe,
            'LevelRequirement': inputEmail,
            'Place': inputPw,
            'Date': rememberMe,
            'Time': inputEmail,
            'Description': inputPw
          })
        })
        const responseData = await response.json()
        if(response.ok){
          console.log('response.OK from login > responnseData :',responseData)
          auth.login();
        }else{
          setIsError(responseData.error)
          throw new Error(responseData.message)
          
        }
      } catch (error) {
        setIsLoading(false)
        console.error(error)
      }
      props.onAddEventItem(newEvent)
    }
    
  };

  return (
    <div className="addEventFormClass">
      <p className="mb-5" id="noticeEvent"> Keep in mind, once you create an event, you will not be able to edit/delete it if other users joined the event unless you make a request to all users and they agree with your request!</p>
      <Form noValidate validated={validated} onSubmit={handleSubmit}>
      <Spinner animation="border" variant="primary"/>
        <Row className="mb-3">
          <Col md="6">
            <Form.Label>Choose a sport</Form.Label>
            <Form.Select value={inputSport} onInput={e => setInputSport(e.target.value)} required>
              <option></option>
              <option>Tennis</option>
              <option>Football</option>
              <option>Table Tennis</option>
              <option>Jogging</option>
              <option>Cycling</option>
              <option>Paintball</option>
              <option>AirSoft</option>
              <option>Skiing</option>
              <option>Basketball</option>
              <option>Work out</option>
              <option>Volleyball</option>
              <option>Badminton</option>
              <option>Ice Skating</option>
              <option>Skating</option>
              <option>Bowling</option>
            </Form.Select>
            <Form.Control.Feedback type="invalid">
              Please provide a valid sport.
            </Form.Control.Feedback>
          </Col>
          <Col md="6">
            <Form.Label>Nr of players including you</Form.Label>
            <Form.Control
              type="number"
              placeholder="ex: 4"
              min="2"
              max="20"
              value={inputNrPlayers} onInput={e => setInputNrPlayers(e.target.value)}
              required
            />
            <Form.Control.Feedback type="invalid">
              Please provide an appropriate nr of players(including you).
            </Form.Control.Feedback>
          </Col>
        </Row>

        <Row className="mb-3">
          <Col md="6">
            <Form.Label>Level Requirement</Form.Label>
            <Form.Select value={inputLevel} onInput={e => setInputLevel(e.target.value)} required>
              <option></option>
              <option>Open to everyone</option>
              <option>Restricted to my level</option>
            </Form.Select>
            <Form.Control.Feedback type="invalid">
              Please provide level requirement.
            </Form.Control.Feedback>
          </Col>
          <Form.Group as={Col} md="6" controlId="validationCustom03">
          <Form.Label>County</Form.Label>
            <Form.Select value={inputCounty} onInput={e => setInputCounty(e.target.value)} required>
              <option></option>
              {countyList.map((item) => <option key={item}> {item}</option>)}
            </Form.Select>
            <Form.Control.Feedback type="invalid">
              Please provide a valid county.
            </Form.Control.Feedback>
          </Form.Group>
        </Row>
        <Row className="mb-3">
          <Form.Group as={Col} md="6" controlId="validationCustom05">
            <Form.Label>City</Form.Label>
            <Form.Control
              type="text"
              value={inputCity} onInput={e => setInputCity(e.target.value)}
              autoComplete="off"
              placeholder="Ex: Iasi"
              required
            />
          </Form.Group>
          <Form.Group as={Col} md="6" controlId="validationCustom05">
            <Form.Label>You can specify a place</Form.Label>
            <Form.Control
              type="text"
              value={inputPlace} onInput={e => setInputPlace(e.target.value)}
              autoComplete="off"
              placeholder="Place: ex: Sport club...(Optional)"
            />
          </Form.Group>
        </Row>
        <Row>
          <Col md="6">
            <Form.Label>Start date</Form.Label>
            <FormControl type="date" min={today} value={inputDate} onInput={e => setInputDate(e.target.value)} required/>
            <Form.Control.Feedback type="invalid">
              Please provide start date
            </Form.Control.Feedback>
          </Col>
          
          <Col md="6">
            <Form.Label>Start time</Form.Label>
            <FormControl type="time" value={inputTime} onInput={e => setInputTime(e.target.value)} required/>
            <Form.Control.Feedback type="invalid">
              Please provide start time
            </Form.Control.Feedback>
          </Col>
        </Row>
        <Row>
          <Col md="12" className="mt-3">
            <FloatingLabel
              controlId="floatingTextarea"
              label="You can write a description.(Optional)"
              className="mb-3"
            >
              <Form.Control as="textarea" value={inputDescription} onInput={e => setInputDescription(e.target.value)}/>
            </FloatingLabel>
          </Col>
        </Row>
        
        <Button type="submit" style={{'float': 'right'}} >Create event</Button>
      </Form>
    </div>
  );
};

