import React, {useState, useContext, useEffect} from "react";
import {AuthContext} from '../../shared/context/auth-context'
import { Form, Button, Spinner } from "react-bootstrap";
import "./LoginForm.css";

const LoginForm = (props) => {
  const [inputEmail, setInputEmail] = useState('')
  const [inputPw, setInputPw] = useState('')
  const [rememberMe, setRememberMe] = useState(false)
  const [isLoading, setIsLoading] = useState(false)
  const [isError, setIsError] = useState()

  const formCheckboxHandler = () => {
    setRememberMe(true)
  }


  const auth = useContext(AuthContext);

  const loginFormHandler = async (e) => {
    e.preventDefault()
    try {
      setIsLoading(true)
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
          // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: JSON.stringify({
          'email': inputEmail,
          'password': inputPw,
          'rememberMe': rememberMe
        })
      })
      const responseData = await response.json()
      if(response.ok){
        console.log('response.OK from login > responnseData :',responseData)
        auth.login();
      }else{
        setIsError(responseData.error)
        throw new Error(responseData.message)
        
      }
    } catch (error) {
      setIsLoading(false)
      console.error(error)
    }
    useEffect(() => {
      let isMounted = true;               // note mutable flag
      someAsyncOperation().then(data => {
        if (isMounted) setState(data);    // add conditional check
      })
      return () => { isMounted = false }; // cleanup toggles value, if unmounted
    }, []);  
    setInputEmail('')
    setInputPw('')
    setRememberMe(false)
    setIsLoading(false)
  }
  return (
    <div className="loginContainer">
      <div className="box">
        <div className="loginForm">
        <h3>Login {isLoading && <Spinner animation="border" variant="primary" />}</h3>
        {isError && <p className="mb-5" id="noticeEvent"> {isError}</p>}
        <Form action="/login" onSubmit={loginFormHandler}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email"  value={inputEmail} onInput={e => setInputEmail(e.target.value)} required/>
            <Form.Text className="text-muted">
              We'll never share your email or password with anyone else.
            </Form.Text>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" value={inputPw} onInput={e => setInputPw(e.target.value)} required/>
          </Form.Group>
          <Form.Group className="mb-3" style={{'float':'right'}} controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Remember me" onClick={formCheckboxHandler} />
          </Form.Group>
          <Button variant="primary" type="submit">
            Login
          </Button>
        </Form>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
