import React from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faFutbol, faTableTennis, faRunning, faDumbbell, faBowlingBall, faSkating, faVolleyballBall, faSkiing } from '@fortawesome/free-solid-svg-icons'
import { Col, Row, Container } from "react-bootstrap";
import blaster from '../../assets/Images/blaster.png'
import './LabelLayout.css'
import SportLabel from "./SportLabel";

const LabelLayout = (props) => {
    // LIST OF COLORS
    // "badge yellow"
    // "badge orange"
    // "badge pink"
    // "badge red"
    // "badge purple"
    // "badge teal"
    // "badge blue"
    // "badge blue-dark"
    // "badge green"
    // "badge green-dark"
    // "badge dark-turquoise"
    // "badge gold"
    // "badge dark"
    // "badge red-dark"
    // "badge purple-dark"
    // "badge pink-dark"

    const handleBadge = (e) => {
        if(e.target.outerHTML.includes('football')){

            //post WHOLE elem to /userprofile/
            console.log('HAS FOOTBALL', e.target.outerHTML)
        }
    }
    return(
        
        <Container onClick={handleBadge}>
            <Row id="label__row">
                <Col><SportLabel className= "badge purple-dark" symbol={<FontAwesomeIcon icon={faFutbol} id="football"/>}  skillLevel="Recreational"/></Col>
                <Col><SportLabel className= "badge green" symbol={<img src="https://img.icons8.com/ios-filled/25/000000/tennis.png" id="tennis" alt="tennis"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge green-dark" symbol={<FontAwesomeIcon icon={faRunning} id="jogging"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge teal" symbol={<img src="https://img.icons8.com/glyph-neue/30/000000/basketball.png" id="basketball" alt="basketball"/>} skillLevel="Beginner"/></Col>
            </Row>
            <Row id="label__row">
                <Col><SportLabel className= "badge red" symbol={<FontAwesomeIcon icon={faDumbbell} id="workout"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge dark" symbol={<FontAwesomeIcon icon={faBowlingBall} id="bowling"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge blue" symbol={<img src="https://img.icons8.com/ios-filled/30/000000/paintball-gun.png" id="paintball" alt="paintball" />} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge purple" symbol={<img src="https://img.icons8.com/ios-filled/30/000000/sports-gun.png" id="airsoft" alt="airsoft"/>} skillLevel="Beginner"/></Col>
            </Row>
            <Row id="label__row">
                <Col><SportLabel className= "badge orange" symbol={<img src={blaster} id="lasertag" alt="lasertag"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge gold" symbol={<FontAwesomeIcon icon={faSkating} id="iceskating"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge dark-turquoise" symbol={<FontAwesomeIcon icon={faSkiing} id="skiing"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge pink" symbol={<FontAwesomeIcon icon={faTableTennis} id="tabletennis"/>} skillLevel="Intermediate"/></Col>
            </Row>
            <Row id="label__row">
                <Col><SportLabel className= "badge blue-dark" symbol={<img src="https://img.icons8.com/ios-filled/30/000000/cycling-road--v1.png" id="cycling" alt="cycling"/>} skillLevel="Advanced"/></Col>    
                <Col><SportLabel className= {props.sports.Volleybal === 'notAssigned' ? 'badge red-dark' : 'badge red-dark disabled'} symbol={<FontAwesomeIcon icon={faVolleyballBall} id="volleyball"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= "badge yellow" symbol={<img src="https://img.icons8.com/ios-filled/29/000000/badminton.png" id="badminton" alt="badminton"/>} skillLevel="Beginner"/></Col>
                <Col><SportLabel className= {props.sports === 'notAssigned' ? 'badge yellow' : 'badge yellow disabled'} symbol={<img src="https://img.icons8.com/ios-filled/29/000000/badminton.png" id="badminton" alt="badminton"/>} skillLevel="Beginner"/></Col>
            </Row>
        </Container>
    )
    
}

export default LabelLayout;
