import React, {useState, useContext} from "react";
import { useSelector, useDispatch } from "react-redux";
import { authActions } from "../../store/index"; 
import {AuthContext} from '../../shared/context/auth-context'
import { Form, Button, Spinner } from "react-bootstrap";
import "./LoginForm.css";

const LoginForm = (props) => {
  const [inputEmail, setInputEmail] = useState('')
  const [inputPw, setInputPw] = useState('')
  const [rememberMe, setRememberMe] = useState(false)
  const [isLoading, setIsLoading] = useState(false)
  const [isError, setIsError] = useState()

  const dispatch = useDispatch();
  useSelector((state) => state.authentication.isAuthenticated)
  useSelector((state) => state.authentication.userId)
  useSelector((state) => state.authentication.token)

  const formCheckboxHandler = () => {
    setRememberMe(true)
  }
  
  const auth = useContext(AuthContext);

  const loginFormHandler = async(e) => {
    e.preventDefault()
    try {
      setIsLoading(true)
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
          // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: JSON.stringify({
          'email': inputEmail,
          'password': inputPw,
          'rememberMe': rememberMe
        })
      })
      const responseData = await response.json()
      if(response.ok){
        console.log('response.OK login > responseData >',responseData)
        dispatch(authActions.login(true)) 
        dispatch(authActions.token(responseData.token)) 
        dispatch(authActions.userId(responseData.userId))

        auth.login(responseData.userId, responseData.token);
        
        let tokenExp;
        if(responseData.rememberMe === true){
          tokenExp = new Date(new Date().getTime() + 1000 * 60 * 60 * 24 * 7 )
        }else{
          tokenExp = new Date(new Date().getTime() + 1000 * 60 * 60 * 3)
        }
        dispatch(authActions.tokenExpiration(token))
        localStorage.setItem('userData', JSON.stringify({userId:responseData.userId, token:responseData.token, expiration: tokenExp.toISOString()}))
        localStorage.setItem('userLoc', JSON.stringify({county:responseData.county, city: responseData.city}))
      }else{
        setIsError(responseData.error)
        setInputPw('')
        setIsLoading(false)
        return new Error(responseData.message)
        
      }
    } catch (error) {
      setIsLoading(false)
      console.error(error)
    }
    
  }
  return (
    <div className="loginContainer">
      <div className="box">
        <div className="loginForm">
        <h3>Login {isLoading && <Spinner animation="border" variant="primary" />}</h3>
        {isError && <p className="mb-5" id="noticeEvent"> {isError}</p>}
        <Form action="/login" onSubmit={loginFormHandler}>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email"  value={inputEmail} onInput={e => setInputEmail(e.target.value)} required/>
            <Form.Text className="text-muted">
              We'll never share your email or password with anyone else.
            </Form.Text>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" value={inputPw} onInput={e => setInputPw(e.target.value)} required/>
          </Form.Group>
          <Form.Group className="mb-3"  style={{'float':'right'}} controlId="formBasicCheckbox">
            <Form.Check type="checkbox" label="Remember me" onClick={formCheckboxHandler} />
          </Form.Group>
          <Button className="login__button" type="submit">
            Login
          </Button>
        </Form>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
