import React, { useContext } from "react";
import { Navbar, Nav, Container, NavDropdown } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { AuthContext } from "../../shared/context/auth-context";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserCircle } from "@fortawesome/free-solid-svg-icons";
import "./HeaderNav.css";

const HeaderNav = () => {
  const auth = useContext(AuthContext);

  const handleLogout = () => {
    auth.logout();
  };

  const modalHandler = () => {
    auth.modalShow();
  };

  let paths;
  if(auth.isLogged){
    paths=(
      <NavDropdown id="basic-nav-dropdown"
        title={
          <div className="main-wrapper__profile">
            <div className="badge1 custom">
              <div className="circle"><FontAwesomeIcon icon={faUserCircle}/></div>
            </div>
          </div>
        }>
        <NavDropdown.Link to="/userprofile">
            >Profile
        </NavDropdown.Link>
        <NavDropdown.Link>
            <Nav.Link onClick={handleLogout}>Logout</Nav.Link>               
        </NavDropdown.Link>
      </NavDropdown>
    )
  }else{
    paths = (
    <Nav.Link variant="primary" onClick={modalHandler}>Register</Nav.Link>
    )
  }

  return (
    <Navbar expand="lg" sticky="top" id="navbar">
      <Container>
        <Navbar.Brand>
          <LinkContainer to="/events">
            <Navbar.Brand>Sport Together</Navbar.Brand>
          </LinkContainer>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <LinkContainer to="/events">
              <Nav.Link>Events</Nav.Link>
            </LinkContainer>
          </Nav>
            {paths}
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default HeaderNav;

