import { createStore } from 'redux';
import { createSlice } from '@reduxjs/toolkit'

const initialAuthState = {
    isLogged: false,
    userId:null,
    token:null,
    login: () => {},
    logout: () => {}
}

createSlice({
    name:'authorization',
    initialState: initialAuthState,
    reducers: {
        isLogged(state, action) {
            state.isLogged = !state.isLogged
        },
        userId() {},
        token() {},
        login() {},
        logout() {}
    }
})

const authorization = (state = initialAuthState, action ) => {
    if(action.type === 'login'){
        return { 
            isLogged: state.isLogged = action.value,
        }
    }
}





const store = createStore({authIsLogged})

export default store;
