import React, { useState } from "react";
import { Col, Row } from "react-bootstrap";
import './BadgeEditForm.css'

const BadgeEditForm = () => {
    const [selectedSport, setSelectedSport] = useState()
    
    const handleSportSelection = (e) => {
        setSelectedSport(e.target.innerText)
        console.log(e.target)
    }

    return (
            <Row className="sportSelectionStyle">
                {!selectedSport && 
                    <>
                        <Col>
                            <div onClick={handleSportSelection}>Tennis</div>
                            <div onClick={handleSportSelection}>Football</div>
                            <div onClick={handleSportSelection}>Table Tennis</div> 
                            <div onClick={handleSportSelection}>Jogging</div>
                            <div onClick={handleSportSelection}>Cycling</div>
                            <div onClick={handleSportSelection}>Paintball</div>
                            <div onClick={handleSportSelection}>Air-Soft</div>
                            <div onClick={handleSportSelection}>Skiing</div>
                        </Col>
                        <Col>
                            <div onClick={handleSportSelection}>Basketball</div>
                            <div onClick={handleSportSelection}>Workout</div>
                            <div>Volleyball</div>
                            <div>Badminton</div>
                            <div>Ice Skating</div>
                            <div>Bowling</div>
                            <div>Laser Tag</div>
                        </Col>
                    </>
                }
            </Row>
          
            // {
            //     selectedSport && 
            //     <>
            //         <div> {selectedSport} </div>
            //         <div> Choose your skill level</div>
            //         <div>Recreational</div>
            //         <div>Beginner</div>
            //         <div>Intermediate</div>
            //         <div>Advanced</div>
            //     </>
            // }
    )
}

export default BadgeEditForm;
