import React,{useState, useContext} from "react";
import './Homepage.css'
import { Col, Row, Container } from "react-bootstrap";
import CarouselHomepage from "../components/Layout/CarouselHomepage";
import LoginForm from "../components/Forms/LoginForm";
import tennisImg from "../assets/Images/tennis-front.jpg"
import { AuthContext } from "../shared/context/auth-context";
import ModalReg from "../components/UI/Modal";

const Homepage = (props) => {
  const [modalShow, setModalShow] = useState(auth.setModal);

  // here button from nav to trigger modal
  const auth = useContext(AuthContext);   

  const hideModalHandler = (modalData) => {
    if(modalData === true){
      setModalShow(true)
    }
  }
  
  
  return (
    <React.Fragment>
      <div><img id='homepageImg' src={tennisImg} alt="..."/> </div>
      <Container >
          <Row>
            
            <Col xl={8}><LoginForm /></Col>
            <ModalReg show={modalShow} onHide1={hideModalHandler}/>
            <Col xl={{span:4, order:'first'}}><CarouselHomepage/></Col>
          </Row>
          <Row>
            <Col xl={12} style={{'position':'relative'}}>
              
            </Col>
          </Row>
      </Container>  
    </React.Fragment>
  );
};

export default Homepage;
