import React, { useState, useEffect } from "react";
import "./UserProfile.css";
import { Col, Row, Container } from "react-bootstrap";
import UserProfileLayout from "../components/UserProfile/UserProfileLayout";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faUserCircle, faPen} from "@fortawesome/free-solid-svg-icons";
import { useSelector, useDispatch } from "react-redux";
import { dialogActions } from "../store/modal";

import tenis from '../assets/Images/tennis.jpg'
import ModalReg from '../components/UI/ModalReg'
import UploadIcon from '../components/Forms/UploadIcon'

const UserProfile = () => {
  const [showDropdown, setShowDropdown] = useState(false)
  const [showModalType, setShowModalType] = useState(0)
  const [coverModal, setCoverModal] = useState(false)
  const [userDetails, setUserDetails] = useState('')


  const dispatch = useDispatch();


  const token = useSelector((state) => state.authentication.token)

  const handleViewProfile = () => {
    setShowModalType(1)
    dispatch(dialogActions.modalToggle())
  }

  const handleChangeProfile = () => {
    setShowModalType(2)
    dispatch(dialogActions.modalTitle('Change profile picture'))
    dispatch(dialogActions.modalToggle())
  };

  const handleChangeCover = () => {
    setCoverModal(true)
    dispatch(dialogActions.modalTitle('Change cover picture'))
    dispatch(dialogActions.modalToggle())
  }

  const handleDropdown = () => {
    setShowDropdown((prevDropdown) => !prevDropdown)
  }

  useEffect(() => {
    const fetchProfileData = async() => {
      let response = await fetch('http://localhost:5000/userprofile', {
        method: 'GET',
        headers:{
          'Content-Type': 'application/json',
          'Authorization': 'Bearer '+token,
        }
      })
      const responseData = await response.json()
      console.log('responseData: ',responseData)
      setUserDetails(responseData)
    }
    fetchProfileData()
  }, [token])



  return (
    <React.Fragment>
    <Container>
      <Row>
        <Col style={{border:'1px solid black'}} md={8}>
          {showModalType === 1 && <ModalReg size="lg" fullscreen="false">
          <img src={userDetails.profileImg} alt="profile pic" 
            style={{width: "700px",
            objectFit: 'cover',
            marginLeft: '35px',
            marginTop:'50px'
          }}/> 
          </ModalReg>}

          {showModalType === 2 && < UploadIcon/>}
          {coverModal && < UploadIcon/>}


          <div className="profile-main">
          <div className="profile-header">
            <img src={userDetails.coverImg} alt="cover" />
            <div className="user-detail">
                <button onClick={handleChangeCover} className="btn btn-dark"> <FontAwesomeIcon icon={faPen}/> Edit cover photo </button>
              <div className="user-image">
                <img src={userDetails.profileImg} alt="profile" />
                <div className="after" onClick={handleDropdown}>
                  <FontAwesomeIcon icon={faEdit} />
                </div>
              </div>
              {showDropdown &&
                <div className="box2 sb10">
                  <div className='listDropdown' onClick={handleViewProfile}><FontAwesomeIcon icon={faUserCircle} style={{'float':'left', 'fontSize':'22px'}}/><span>View profile picture</span></div>
                  <div className='listDropdown' onClick={handleChangeProfile}><FontAwesomeIcon icon={faEdit} style={{'float':'left', 'fontSize':'22px'}}/><span>Change profile picture</span></div>
              </div>}
              </div>
            </div>
          </div>
        </Col>
        <Col style={{border:'1px solid black'}}  md={4}>
          sm=4
        </Col>
      </Row>
      <Row  style={{height:'400px'}}>
        <Col style={{border:'1px solid black'}}  md={4}><img src={tenis} style={{width:'100%', height:'100%', objectFit:'cover'}}/></Col>
        <Col style={{border:'1px solid black'}}  md={4}>sm=true</Col>
        <Col style={{border:'1px solid black'}}  md={4}>sm=true</Col>
      </Row>
    </Container>


    <Container>
      <UserProfileLayout />
    </Container>
    </React.Fragment>
  );
};

export default UserProfile;
