import React, { useContext, useEffect } from "react";
import './Homepage.css'
import { Col, Row, Container } from "react-bootstrap";
import CarouselHomepage from "../components/Layout/CarouselHomepage";
import LoginForm from "../components/Forms/LoginForm";
import RegisterForm from '../components/Forms/RegisterForm'
import tennisImg from "../assets/Images/tennis-front.jpg"
import ModalReg from "../components/UI/ModalReg";
import { AuthContext } from "../shared/context/auth-context";


const Homepage = (props) => {
  const auth = useContext(AuthContext)
  const backdropFunction = () => {
    auth.modalShow()
  }

  const rent111 = document.getElementById("rent111");
  const sell111 = document.getElementById("sell111");
  const explore111 = document.getElementById("explore111");
  
  
  useEffect(() => {
    const timeOuts = () => {
      setTimeout(() => {
        explore111.id = "explore111";
        rent111.id = "rent112";
        setTimeout(() => {
          rent111.id = "rent111";
          sell111.id = "sell112";
          setTimeout(() => {
            sell111.id = "sell111";
            explore111.id = "explore112";
          }, 1500);
        }, 1500);
      }, 1500);
    };
    timeOuts();
    setInterval(timeOuts, 4500);
  },[explore111, rent111, ])


  return (
    <React.Fragment >
      <div id="welcomeBox">
      <Container >
      <Row style={{'marginTop':'20px'}}>
        <Col>
          <div className="container123">
            <div id="box111">
              <div>
                <span id={rent111}>Connect.</span>
              </div>
              <div>
                <span id={sell111}>Play.</span>
              </div>
              <div>
                <span id={explore111}>Engage.</span>
              </div>
            </div>
          </div>
        </Col>
        <Col>
          <LoginForm />
          <div className="d-flex justify-content-center">
            <h3>Not registered yet?</h3>
          </div>
          <div>
            <h3 className="d-flex justify-content-center">
              Create an account, it only takes one minute!
            </h3>
          </div>  
        </Col>
      </Row>
      </Container>
      </div>
      <div><img id='homepageImg' src={tennisImg} alt="..."/> </div>
      <Container >
          <Row>
            <ModalReg show={props.showMod} onHide={backdropFunction} title="Please Take a min"> <RegisterForm/> </ModalReg>
            <Col xl={{span:4, order:'first'}}><CarouselHomepage/></Col>
          </Row>
      </Container>  
    </React.Fragment>
  );
};

export default Homepage;
