import {Button, Modal} from 'react-bootstrap';
import React from "react";
import { useSelector, useDispatch } from 'react-redux';
import { dialogActions } from '../../store/modalAndAlerts';
import './Modal.css'

function ModalReg(props) {

  const dispatch = useDispatch();
  const modalShow = useSelector((state) => state.dialog.modalToggle)

  const modalCloseHandler = () => {
    dispatch(dialogActions.modalToggle())
  }
  const someTestFunction = () => {
    dispatch(dialogActions.modalToggle())
  }

  return (
    <Modal show={modalShow} onHide={someTestFunction} size={props.size} style={props.customStyle}  className="d-flex justify-content-center"> 
        <Modal.Header closeButton>
          <Modal.Title>{props.title}</Modal.Title>
        </Modal.Header>
        <Modal.Body> {props.children} </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={modalCloseHandler}>
            Close
          </Button>
          {/* <Button variant="primary" onClick={modalCloseHandler}>
            Save Changes
          </Button> */}
        </Modal.Footer>
      </Modal>
  );
}

export default ModalReg;
