import React from "react";
import './Alert.css'
import { Alert } from "react-bootstrap";
import { useSelector } from "react-redux";


const AlertDismissible = (props) => {
  const alertVariant = useSelector((state) => state.alert.alertVariant)
  const alertTitle = useSelector((state) => state.alert.alertTitle)
  const alertBody = useSelector((state) => state.alert.alertBody)


  return (
    <Alert variant={alertVariant} >
      <Alert.Heading className="center">{alertTitle} </Alert.Heading>
      <div>{alertBody}</div>
    </Alert>
  );
}
  
  

  export default AlertDismissible
