import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { dialogActions } from "../../store/modalAndAlerts";
import ModalReg from "../UI/Modal";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faUserCircle } from "@fortawesome/free-solid-svg-icons";
import tennis from "../../assets/Images/tennis.jpg";
import LabelLayout from "./LabelLayout";
import "./UserProfileLayout.css";
import UploadIcon from "../Forms/UploadIcon";

const UserProfileLayout = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [showModalType, setShowModalType] = useState(0);
  const [userDetails, setUserDetails] = useState("");

  const dispatch = useDispatch();
  const firstName = useSelector(state => state.user.userFirst);
  const lastName = useSelector(state => state.user.userLast);
  const county = useSelector(state => state.user.userCounty);
  const profilePic = useSelector(state => state.user.userProfilePic);
  const reviewRating = useSelector(state => state.user.userReviewRating);
  // const username = useSelector((state) => state.user.username)
  // const city = useSelector((state) => state.user.userCity)
  // const coverPic = useSelector((state) => state.user.userCoverPic)
  // const description = useSelector((state) => state.user.userDescription)
  // const sportInterests = useSelector((state) => state.user.userSportInterests)
  // const createdEvents = useSelector((state) => state.user.userCreatedEvents)
  // const joinedEvents = useSelector((state) => state.user.userJoinedEvents)
  // const matchesPlayed = useSelector((state) => state.user.userMatchesPlayed)

  const token = useSelector(state => state.authentication.token);

  const handleViewProfile = () => {
    setShowModalType(1);
    dispatch(dialogActions.modalToggle());
  };

  const handleChangeProfile = () => {
    setShowModalType(2);
    dispatch(dialogActions.modalToggle());
  };

  const handleDropdown = () => {
    setShowDropdown(prevDropdown => !prevDropdown);
  };

  useEffect(() => {
    const fetchProfilePic = async () => {
      let response = await fetch("http://localhost:5000/userprofile", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer " + token,
        },
      });
      const responseData = await response.json();
      console.log("responseData: ", responseData);
      setUserDetails(responseData);
    };
    if (showModalType) fetchProfilePic();
  }, [token]);

  return (
    <div>
      {showModalType === 1 && ( <ModalReg size="lg">
          <img
            src={tennis}
            alt="profile pic"
            style={{
              width: "700px",
              objectFit: "cover",
              marginLeft: "35px",
              marginTop: "50px",
            }}/>
        </ModalReg>)}
      {showModalType === 2 ? (
        <ModalReg title="Change profile picture" size="lg">
          <UploadIcon />
        </ModalReg>
      ) : (
        <ModalReg title="Change profile picture" size="lg">
          <UploadIcon />
        </ModalReg>
      )}
      <div className="profile-main">
        <div className="profile-header">
          <img src={"asdsad"} alt="cover" />
          <div className="d-flex justify-content-center">
            <div id="trapezoid"></div>
          </div>
          <div className="user-detail">
            <div className="footer">
              <div className="footer-box">
                <i className="fa fa-facebook"></i>
              </div>
              <div className="footer-box">
                <i className="fa fa-twitter"></i>
              </div>
              <div className="footer-box">
                <i className="fa fa-linkedin"></i>
              </div>
              <img
                src="https://img.icons8.com/ios-filled/48/000000/tiktok--v1.png"
                alt="tiktokIcon"
              />
              <div className="footer-box">
                <i className="fa fa-instagram"></i>
              </div>
            </div>

            <div className="user-image">
              <img src={userDetails.profileImg} alt="profile" />
              <div className="after" onClick={handleDropdown}>
                <FontAwesomeIcon icon={faEdit} />
              </div>
            </div>
            {showDropdown && (
              <div className="box2 sb10">
                <div className="listDropdown" onClick={handleViewProfile}>
                  <FontAwesomeIcon
                    icon={faUserCircle}
                    style={{ float: "left", fontSize: "22px" }}
                  />
                  <span>View profile picture</span>
                </div>
                <div className="listDropdown" onClick={handleChangeProfile}>
                  <FontAwesomeIcon
                    icon={faEdit}
                    style={{ float: "left", fontSize: "22px" }}
                  />
                  <span>Change profile picture</span>
                </div>
              </div>
            )}
            <h2>{userDetails.firstName + " " + userDetails.lastName}</h2>
            <div className="post-label">
              <span>DUMMY Animal Lover</span>
              <span>DUMMY Sport</span>
              <span>DUMMY Cooking</span>
            </div>
            <div className="job__title">
              Dummy Founder and CEO at <strong>Dummy NewSpot</strong>
            </div>
            <p>
              <i className="fa fa-map-marker" aria-hidden="true"></i>
              <span className="city__label">{userDetails.county}</span>
            </p>
            <div className="rating__label">
              Rating: {userDetails.reviewRating}
            </div>
            <div className="bio-box">
              <p>Bio</p>
              <div className="desc">
                (Dummy Lorem ipsum dolor sit amet, consectetur adipisicing elit,
                sed do eiusmod tempor incididunt ut labore et dolore magna
                aliqua. Ut enim ad minim veniam.)DUMMY
              </div>
            </div>
          </div>
        </div>
      </div>
      <div>
        <div className="sport-labels">
          <LabelLayout />
        </div>
      </div>
    </div>
  );
};

export default UserProfileLayout;
