import React from "react";
import { Alert } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { alertActions } from "../../store/alert";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTimes } from "@fortawesome/free-solid-svg-icons";

const AlertDismissible = (props) => {
  const dispatch = useDispatch;
  const alertActive = useSelector(state => state.alert.alertToggle)
  const alertVariant = useSelector((state) => state.alert.alertVariant)
  const alertTitle = useSelector((state) => state.alert.alertTitle)
  const alertBody = useSelector((state) => state.alert.alertBody)


  const handleClose = () => {
    dispatch(alertActions.alertToggle())
  }

  return (
    <Alert variant={alertVariant}>
      <Alert.Heading className="center">{alertTitle} <FontAwesomeIcon className="floatRight" onClick={handleClose} icon={faTimes} /></Alert.Heading>
      
    </Alert>
  );
}
