import "./AddEventForm.css";
import React, { useState, useContext, useEffect } from "react";
import Select from 'react-select'
import countyList from "../../utils/CitiesAndCountiesRo/countyList";
import countyCityList from "../../utils/CitiesAndCountiesRo/countyCityList";
import sportOptions from "../../utils/sportListAndOptions/sportOptions";
import { Spinner } from "react-bootstrap";
import {Form, Col, Row, Button, FloatingLabel, FormControl} from "react-bootstrap";
import { AuthContext } from "../../shared/context/auth-context";
import { ReactComponent as LogoSvg1 } from '../../assets/Images/SportTogetherJustLogoSVG1.svg';

var today = new Date();
var dd = today.getDate();
var mm = today.getMonth()+1; //January is 0 so need to add 1 to make it 1!
var yyyy = today.getFullYear();

if(dd<10) dd='0'+dd;
if(mm<10) mm='0'+mm; 
today = yyyy+'-'+mm+'-'+dd;



const AddEventForm = (props) => {
  const auth = useContext(AuthContext)

  const [isLoading, setIsLoading] = useState(false)
  const [validated, setValidated] = useState(false);
  const [isError, setIsError] = useState(false);
  const [inputSport, setInputSport] = useState('')
  const [inputNrPlayers, setInputNrPlayers] = useState('')
  const [inputLevel, setInputLevel] = useState('')
  const [inputCounty, setInputCounty] = useState('Select County')
  const [inputCity, setInputCity] = useState('Select City')
  const [inputPlace, setInputPlace] = useState('')
  const [inputDate, setInputDate] = useState('')
  const [inputTime, setInputTime] = useState('')
  const [inputDescription, setInputDescription] = useState('')
  const [listCounties, setListCounties] = useState([])
  const [listCities, setListCities] = useState([])
  const [buttonClass, setButtonClass] = useState('')

  function capitalizeFirstLetter(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }
  function reverseDate(string){
    return string = string.split("-").reverse().join("-");
  }

  useEffect(()=>{
    let lstCounties = []
    for (let i of countyList){
      lstCounties.push({value: i, label: i})
    }
    setListCounties(lstCounties)
  }, [])

  const countyOptionChange = (obj) => {
    setInputCounty(obj.value)
    const countyNameList = countyCityList[obj.value]
    let lstCities = []
      for (let i of countyNameList){
        lstCities.push({value: i, label: i})
      }
    setListCities(lstCities)
  }

  const cityOptionChange = (obj) => {
    console.log(obj.value)
    setInputCity(obj.value)
  }

  const sportChoiceHandler = (opt) => {
    console.log(opt)
    setInputSport(opt.value)
  }
  
  const btnClass1 = () => {
    setInputLevel(true)
    console.log('CLICK BTN1')
    setButtonClass('btnActive1')
  }
  const btnClass2 = () => {
    setInputLevel(false)
    console.log('CLICK BTN2')
    setButtonClass('btnActive2')
  }

  const currentLocationHandler = () => {
    const cc = JSON.parse(localStorage.getItem('userLoc'))
    console.log('County >:',cc.county)
    console.log('City :>',cc.city)
    setInputCounty(cc.county)
    setInputCity(cc.city)
  }
  // SUBMIT HANDLER !
  const handleSubmit = async (event) => {
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.preventDefault();
    }
    setValidated(true);
    if (form.checkValidity() === true) {
      event.preventDefault();
      setIsLoading(true)
      if(input)
      try {
        const response = await fetch('http://localhost:5000/createEvent', {
                    method: 'POST',
                    headers: new Headers({
                      'Authorization': 'Bearer '+auth.token,
                      'Accept': 'application/json',
                      'Content-Type': 'application/json',
                    }),
                    body: JSON.stringify({
                      'Sport' : inputSport,
                      'County' : inputCounty,
                      'City' : capitalizeFirstLetter(inputCity),
                      'NrPlayers' : inputNrPlayers,
                      'LevelRequirement' : inputLevel,
                      'Place' : inputPlace,
                      'Date' : reverseDate(inputDate),
                      'Time' : inputTime,
                      'Description' : inputDescription,
                    }),
        });
        const responseData = await response.json()
        if(response.ok){
          console.log('response.OK from /createEvent > responnseData :', responseData)
          // isSuccess used to show message
        }
      } catch (error) {
        setIsError(error)
        setIsLoading(false)
        console.log(error)
      }
      setTimeout(() => {
        props.onAddEventItem(false)
      }, 1000);
    }

    setTimeout(() => {
      setIsLoading(false)
    }, 500);
  };

  return (
    <div className="addEventFormClass">
      <p className="mb-5" id="noticeEvent"> Keep in mind, once you create an event, you will not be able to edit/delete it if other users joined the event unless you make a request to all users and they agree with your request!</p>
      <Form noValidate validated={validated} onSubmit={handleSubmit}>

      {isLoading && 
      <Row className="mb-3 justify-content-center"> 
        <Col md='2' className="d-flex justify-content-center"> <Spinner animation="border" role="status"/> </Col> 
      </Row>}
      {isError && 
        <p className="mb-5" id="noticeEvent">{isError}</p>
      }
        <Row className="mb-3">
          <Col md="6">
            <Form.Label>Choose a sport</Form.Label>
            <Select options={sportOptions} value={inputSport.value} onChange={(option) => sportChoiceHandler(option)}/>
          </Col>
          <Col md="6">
            <Form.Label>Nr of players including you</Form.Label>
            <Form.Control
              type="number"
              placeholder="ex: 4"
              min="2"
              max="20"
              value={inputNrPlayers} onInput={e => setInputNrPlayers(e.target.value)}
              required
            />
            <Form.Control.Feedback type="invalid">
              Please provide an appropriate nr of players(including you).
            </Form.Control.Feedback>
          </Col>
        </Row>

        <Row className="mb-3">
          <Col md="6" style={{'textAlign':'center'}}>
            <Form.Label >Level Requirement</Form.Label>
            <div className="choiceBtns">
              <Form.Check type="radio" aria-label="option 1"> <button type="button" id = { buttonClass === 'btnActive1' ? 'btnActive1' : ''} value={true} onClick={btnClass1}> Only my level </button> </Form.Check>
              <Form.Check type="radio" aria-label="option 2"> <button type="button"  id = { buttonClass === 'btnActive2' ? 'btnActive2' : ''} value={false} onClick={btnClass2}> Open to everyone</button> </Form.Check>
            </div>
          </Col>
          <Form.Group as={Col} md="6" controlId="validationCustom03">
          <Form.Label>County</Form.Label> <button type="button" className='currentLocationBtn' onClick={currentLocationHandler}> <LogoSvg1/> </button>
            <Select options ={listCounties} value={inputCounty} placeholder={inputCounty}  onChange={(option) => countyOptionChange(option)}/>
          </Form.Group>
        </Row>
        <Row className="mb-3">
          <Form.Group as={Col} md="6" controlId="validationCustom05">
            <Form.Label>City</Form.Label>
            <Select options = {listCities} value={inputCity} placeholder={inputCity}  onChange={(opt) => cityOptionChange(opt)}/>
          </Form.Group>
          <Form.Group as={Col} md="6" controlId="validationCustom05">
            <Form.Label>You can specify a place</Form.Label>
            <Form.Control
              type="text"
              value={inputPlace} onInput={e => setInputPlace(e.target.value)}
              autoComplete="off"
              placeholder="Place: ex: Sport club...(Optional)"
            />
          </Form.Group>
        </Row>
        <Row>
          <Col md="6">
            <Form.Label>Start date</Form.Label>
            <FormControl type="date" min={today} value={inputDate} onInput={e => setInputDate(e.target.value)} required/>
            <Form.Control.Feedback type="invalid">
              Please provide start date
            </Form.Control.Feedback>
          </Col>
          
          <Col md="6">
            <Form.Label>Start time</Form.Label>
            <FormControl type="time" value={inputTime} onInput={e => setInputTime(e.target.value)} required/>
            <Form.Control.Feedback type="invalid">
              Please provide start time
            </Form.Control.Feedback>
          </Col>
        </Row>
        <Row>
          <Col md="12" className="mt-3">
            <FloatingLabel
              controlId="floatingTextarea"
              label="You can write a description.(Optional)"
              className="mb-3"
            >
              <Form.Control as="textarea" value={inputDescription} onInput={e => setInputDescription(e.target.value)}/>
            </FloatingLabel>
          </Col>
        </Row>
        
        {!isLoading && <Button type="submit" style={{'float': 'right'}} >Create event</Button>}
        {isLoading && <Button variant="primary" style={{'float': 'right'}} disabled>
          <Spinner
            as="span"
            animation="grow"
            size="sm"
            role="status"
            aria-hidden="true"
          />
          Loading...
        </Button>}
      </Form>
    </div>
  );
};

export default AddEventForm;
