import React, {useState} from "react";
import { Button, Spinner } from "react-bootstrap";
import "./UploadIcon.css";

const UploadIcon = () => {
  const [inputFile, setInputFile] = useState()
  const [isLoading, setIsLoading] = useState(false)

  const handleFileInput = (e) => {
    setInputFile(e.target.value)
  }

  const handleSubmit = async (event) => {
    event.preventDefault();
    setIsLoading(true);
    // try {
    //   const response = await fetch('http://localhost:5000/uploadImage', {
    //     method: 'POST',
    //     headers: {
    //       'Content-Type': 'application/json'
    //     },
    //     body: asdsad,
    //   })


    // } catch (error) {
      
    // }
    console.log(inputFile,'fileName')
    setIsLoading(false)
  }
  
  return (
    <form encType="multipart/form-data" onSubmit={handleSubmit}>
      <div className="wrapper">
        <div className="file-upload">
          <input type="file" id="image" name="image" onInput={handleFileInput} value={inputFile}/>
          <i className="fa fa-arrow-up" style={{'fontSize':'40px'}}></i>
        </div>
      </div>
      {isLoading && <Spinner animation="border" variant="primary"/>}
      <Button type='submit' style={{borderColor:'transparent'}}>Preview</Button>
    </form>
  );
};


export default UploadIcon;
