import React, { useEffect, useState } from "react";
import EventItem from "./EventItem";

const EventList = (props) => {
    const [events, setEvents] = useState(props.eventsList)
    useEffect(() => {
        let x = props.eventsList.filter(item => item.Sport === props.allFilters.sportFilter)
        setEvents(x)
       

        x.filter(items => items.Level === props.allFilters.difficultyFilter)
        console.log(x)
    },[props.allFilters, props.eventsList])

    return (
        <div>
            {props.eventsList.map((item) => (
            <EventItem key = {item.id}
                        sport = {item.Sport}
                        level = {item.Level}
                        city = {item.City}
                        location = {item.ExactLocation}
                        description = {item.Description}
                        date = {item.Date}
                        time = {item.Time}
                        nrPlayers = {item.NrPlayers}
                        allowed = {item.LevelRequirement}
            /> 
            ))}
        </div>
    )
}

export default EventList;

