import React, {useState, useContext} from "react";
import { Form, Button, Spinner } from "react-bootstrap";
import { AuthContext } from "../../shared/context/auth-context";
import countyList from "../../assets/CountyList/countyList";
import "./RegisterForm.css";

const RegisterForm = () => {
  const [inputEmail, setInputEmail] = useState('')
  const [inputUsername, setInputUsername] = useState('')
  const [inputFirst, setInputFirst] = useState('')
  const [inputLast, setInputLast] = useState('')
  const [inputCounty, setInputCounty] = useState('')
  const [inputPw, setInputPw] = useState('')
  const [inputConfirmPw, setInputConfirmPw] = useState('')
  const [isLoading, setIsLoading] = useState(false)
  const [isError, setIsError] = useState()

  const auth = useContext(AuthContext)

  const registerFormHandler = async (event) => {
    event.preventDefault();
    setIsLoading(true)
    try {
      const response = await fetch('http://localhost:5000/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
          // 'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: JSON.stringify({
          'email': inputEmail,
          'username': inputUsername,
          'firstName': inputFirst,
          'lastName': inputLast,
          'county' : inputCounty,
          'password': inputPw,
          'confirmPw': inputConfirmPw,
        })
      })
      const responseData = await response.json()
      if(!response.ok){
        setIsError(responseData.error)
        throw new Error(responseData.message)
      }else{
        auth.login(responseData.userId, responseData.token)
        console.log(responseData)
      }
    } catch (error) {
      console.error(error)
        setIsLoading(false)
    }
      
        
    setInputEmail('')
    setInputCi('')
    setInputUsername('')
    setInputFirst('')
    setInputLast('')
    setInputPw('')
    setInputConfirmPw('')
    setIsLoading(false)
  }
 
  return (
    <div className="registerContainer">
      <div className="boxRegister">
        <div className="registerForm">
        <h3>Register {isLoading && <Spinner animation="border" variant="primary"/>}</h3>
        {isError && <p className="mb-5" id="noticeEvent"> {isError}</p>}
        <Form onSubmit={registerFormHandler}>
            <Form.Text className="text-muted">
              We'll never share your data with anyone else.
            </Form.Text>
            <br/>
          <Form.Group className="mb-3" controlId="formEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" value={inputEmail} onInput={e => setInputEmail(e.target.value)} placeholder="Enter email" required/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formUserName">
            <Form.Label>User Name</Form.Label>
            <Form.Control type="text" value={inputUsername} onInput={e => setInputUsername(e.target.value)} placeholder="User name" required/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formFirstName">
            <Form.Label>First Name</Form.Label>
            <Form.Control type="text" value={inputFirst} onInput={e => setInputFirst(e.target.value)} placeholder="First name" required/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formLastName">
            <Form.Label>Last Name</Form.Label>
            <Form.Control type="text" value={inputLast} onInput={e => setInputLast(e.target.value)} placeholder="Last name" required/>
          </Form.Group>
          <Form.Group controlId="formCounty">
          <Form.Label>County</Form.Label>
            <Form.Select value={inputCounty} onInput={e => setInputCounty(e.target.value)} required>
              <option></option>
              {countyList.map((item) => <option key={item}> {item}</option>)}
            </Form.Select>
            <Form.Control.Feedback type="invalid">
              Please provide a valid county.
            </Form.Control.Feedback>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" value={inputPw} onInput={e => setInputPw(e.target.value)} placeholder="Password" required/>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formConfirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control type="password" value={inputConfirmPw} onInput={e => setInputConfirmPw(e.target.value)} placeholder="Confirm Password" required />
          </Form.Group>

          <Button variant="primary"  style={{'float':'right'}} type="submit">
            Register
          </Button>
        </Form>
        </div>
      </div>
    </div>
  );
};

export default RegisterForm;
