import React, { useState } from "react";
import './Homepage.css'
import HeaderNav from "../components/Layout/HeaderNav";
import { Col, Row, Container } from "react-bootstrap";
import CarouselHomepage from "../components/Layout/CarouselHomepage";
import LoginForm from "../components/Forms/LoginForm";
import RegisterForm from "../components/Forms/RegisterForm";
import Image from "../assets/Images/checkingWatch1.png"
const Homepage = (props) => {
  const [form, setForm] = useState(true);

  const formHandler = (data) => {
    setForm(data);
    console.log(form);
  };

  return (
    <React.Fragment>
      <HeaderNav onShowForm={formHandler} />
        <div><img id='logo' src={Image} alt="assa"/> </div>
        <Container>
            <Row>
            <Col xs={{span:4, order: 'last' }} >
                <CarouselHomepage />
                
            </Col>
            {form && <Col xs={{span:4, order: 'first' }}><LoginForm /></Col>}
            {!form && <Col xs={{ order: 'first' }}><RegisterForm /></Col>}
            </Row>
        </Container>  
    </React.Fragment>
  );
};

export default Homepage;
