import React, {useState} from "react";
import { Button, Spinner } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";
import { dialogActions } from '../../store/modalAndAlerts'
import { userActions } from "../../store/userData";
import ModalReg from "../UI/Modal";
import "./UploadIcon.css";

const UploadIcon = (props) => {
  const [isLoading, setIsLoading] = useState(false)
  const [selectedFile, setSelectedFile] = useState()
  const [responseOk, setResponseOk] = useState(false)
  const [preview, setPreview] = useState()

  const dispatch = useDispatch()
  useSelector((state) => state.dialog.modalToggle)

  const token = useSelector((state) => state.authentication.token)

  const title = useSelector((state) => state.dialog.modalTitle)


  const handleBack = () => {
    setResponseOk(false)
  }

  const handleModalClose = () => {
    dispatch(dialogActions.modalToggle())
  }

  const handleImage = (event) => {
		setSelectedFile(event.target.files[0]);
	};

  const handlePreview = () => {
    setIsLoading(true);
    const formData = new FormData();
		formData.append('cover', selectedFile);
    const objectUrl = URL.createObjectURL(selectedFile)
    setPreview(objectUrl)
    setResponseOk(true)
    setIsLoading(false)
  };

  const handleUpload = async () => {
    let pictureType;
    if(title === 'Change profile picture'){
      pictureType = 'profile'
    }else if(title === 'Change cover picture'){
      pictureType = 'cover'
    }
    setIsLoading(true)
    const formData = new FormData();
		formData.append(pictureType, selectedFile);
    const response = await fetch('http://localhost:5000/uploadimage',
    {
      method: 'POST',
      headers:{'Authorization': "Bearer " + token},
      body: formData
    })
    const responseData = await response.json()
    if(response.ok){
      console.log('responseOK Upload', responseData)
    }else{
      console.log('response NOT OK', responseData)
      setIsLoading(false)
    }
    setIsLoading(false)
    setResponseOk(false)
    setPreview(null)
    dispatch(dialogActions.modalToggle())
  }
	
  return (
    <ModalReg title={title} size="lg">
      {!responseOk && <form encType="multipart/form-data" className='formStyleButtons'>
        <div className="wrapper"  >
          <div className="file-upload">
            <input type="file" id="fileUpload" name="profile" onChange={handleImage}/>
            <i className="fa fa-arrow-up" style={{'fontSize':'40px'}}></i>
          </div>
        </div>
        <Button onChange={handlePreview} disabled={isLoading}>Preview</Button>
        <Button onClick={handleModalClose}  disabled={isLoading}>Close</Button>
      </form>}

      {responseOk && <form className='formStyleButtons1'>
        {isLoading && <div>Please don't close this window while we processing your request</div>}
        <img src={preview} alt="errorIMG"/>
        {isLoading && <Spinner animation="border" variant="primary"/>}
        <Button onClick={handleUpload}  disabled={isLoading}>Upload</Button>
        <Button onClick={handleModalClose}  disabled={isLoading}>Close</Button>
        <Button onClick={handleBack}  disabled={isLoading} style={{float:'left', marginLeft:'25px'}}>Back</Button>
      </form>}
    </ModalReg>
  );
};


export default UploadIcon;
